mapper.select:
SELECT 
	t.ID id,
	t.collect_ID collectId,
	t.FAVORITE_ID favoriteId,
	t.FILE_TYPE fileType,
	t.Physical_PATH physicalPath,
	t.DM_NAME dmName,
	t.DM_ALIAS dmAlias,
	t.KM_CONTENT kmContent,
	t.KEY_WORDS keyWords,
	t.DM_TYPE dmType,
	t.AUTHOR author,
	t.KM_TITLE kmTitle,
	t.CURRENT_VERSION currentVersion,
	t.DM_SIZE dmSize,
	t.SEQ seq,
	t.WEIGHT weight,
	t.DM_CREATE_USER dmCreateUser,
	t.KM_RELEASE_DATE kmReleaseDate,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM toa_archive_favorite t


		and t.ID = #{id}



		and t.collect_ID = #{collectId}



		and t.FAVORITE_ID = #{favoriteId}



		and t.FILE_TYPE = #{fileType}



		and t.Physical_PATH = #{physicalPath}



		and t.DM_NAME = #{dmName}



		and t.DM_ALIAS = #{dmAlias}



		and t.KM_CONTENT = #{kmContent}



		and t.KEY_WORDS = #{keyWords}



		and t.DM_TYPE = #{dmType}



		and t.AUTHOR = #{author}



		and t.KM_TITLE = #{kmTitle}



		and t.CURRENT_VERSION = #{currentVersion}



		and t.DM_SIZE = #{dmSize}



		and t.SEQ = #{seq}



		and t.WEIGHT = #{weight}



		and t.DM_CREATE_USER = #{dmCreateUser}



		and t.KM_RELEASE_DATE = #{kmReleaseDate}


		and t.KM_RELEASE_DATE >= #{kmReleaseDateBegin}


		and t.KM_RELEASE_DATE <= #{kmReleaseDateEnd}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

SELECT count(ID) 
FROM toa_archive_favorite t


		and t.ID = #{id}



		and t.collect_ID = #{collectId}



		and t.FAVORITE_ID = #{favoriteId}



		and t.FILE_TYPE = #{fileType}



		and t.Physical_PATH = #{physicalPath}



		and t.DM_NAME = #{dmName}



		and t.DM_ALIAS = #{dmAlias}



		and t.KM_CONTENT = #{kmContent}



		and t.KEY_WORDS = #{keyWords}



		and t.DM_TYPE = #{dmType}



		and t.AUTHOR = #{author}



		and t.KM_TITLE = #{kmTitle}



		and t.CURRENT_VERSION = #{currentVersion}



		and t.DM_SIZE = #{dmSize}



		and t.SEQ = #{seq}



		and t.WEIGHT = #{weight}



		and t.DM_CREATE_USER = #{dmCreateUser}



		and t.KM_RELEASE_DATE = #{kmReleaseDate}


		and t.KM_RELEASE_DATE >= #{kmReleaseDateBegin}


		and t.KM_RELEASE_DATE <= #{kmReleaseDateEnd}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from toa_archive_favorite
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_archive_favorite t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
