mapper.select:
SELECT
t.id id,
t.file_name fileName,
t.file_path filePath,
t.document_id documentId,
t.fize_size fizeSize,
t.file_type fileType,
t.FORM_CONTENT formContent
FROM toa_archive_filing t


		and t.id = #{id}



		and t.file_name = #{fileName}



		and t.file_type in (1,2)



		and t.file_path = #{filePath}



		and t.document_id = #{documentId}



		and t.fize_size = #{fizeSize}



		and t.file_type=#{fileType}


		and t.FORM_CONTENT=#{formContent}

SELECT count(id)
FROM toa_archive_filing t


		and t.id = #{id}



		and t.file_name = #{fileName}



		and t.file_path = #{filePath}



		and t.document_id = #{documentId}



		and t.fize_size = #{fizeSize}



		and t.file_type=#{fileType}



		and t.FORM_CONTENT=#{formContent}

mapper.delete:
delete from toa_archive_filing
where id in

	#{primaryKey}

mapper.update:
UPDATE toa_archive_filing t set t.delete_flag = 1,t.modify_user =
#{modifyUser},t.modify_date = #{modifyDate}
where ID in

	#{primaryKey}

UPDATE toa_archive_filing set

	file_name = #{fileName},


	file_path = #{filePath},


	document_id = #{documentId},


	fize_size = #{fizeSize},


	file_type=#{fileType}


	FORM_CONTENT=#{formContent}

MODIFY_DATE = #{modifyDateNew} where id = #{id} and MODIFY_DATE =
#{modifyDate}

mapper.select:
SELECT t.id id, t.file_name fileName, t.file_path filePath, t.document_id documentId, t.fize_size fizeSize, t.file_type fileType, t.FORM_CONTENT formContent FROM toa_archive_filing t
and t.id = #{id}
and t.document_id = #{documentId}
and t.file_type in (1,2)
