mapper.select:
SELECT
t.ID id,
t.FOLDER_TYPE folderType,
t.NODE_TYPE nodeType,
t.PARENT_FOLDER_ID parentFolderId,
t.FOLDER_NAME folderName,
t.FOLDER_PATH folderPath,
t.DM_IN_RECYCLE dmInRecycle,
t.KM_MANAGER_ID kmManagerId,
t.KM_SORT kmSort,
t.KM_DEADLINE kmDeadline,
t.WEIGHT weight,
t.KM_STANDARD_COUNT kmStandardCount,
t.KM_APP_FLAG kmAppFlag,
t.KM_STANDARD_TIME kmStandardTime,
t.PERMISSION_VALUE permissionValue,
t.DELETE_FLAG deleteFlag,
t.CREATE_USER createUser,
t.CREATE_USER_DEPT createUserDept,
t.CREATE_USER_ORG createUserOrg,
t.CREATE_DATE createDate,
t.MODIFY_USER modifyUser,
t.MODIFY_DATE modifyDate,
t.EXT_DATE1 extDate1,
t.EXT_DATE2 extDate2,
t.EXT_NUM1 extNum1,
t.EXT_NUM2 extNum2,
t.EXT_NUM3 extNum3,
t.EXT_STR1 extStr1,
t.EXT_STR2 extStr2,
t.EXT_STR3 extStr3,
t.EXT_STR4 extStr4,
t.EXT_STR5 extStr5,
u.DISPLAY_NAME owner

FROM toa_archive_folder t LEFT JOIN toa_sys_user u on t.create_user =u.ID


		and t.ID = #{id}



		and t.FOLDER_TYPE = #{folderType}



		and t.NODE_TYPE = #{nodeType}



		and t.PARENT_FOLDER_ID = #{parentFolderId}



		and t.FOLDER_NAME = #{folderName}




		and dbms_lob.instr(t.FOLDER_PATH,#{folderPath},1,1) > 0



		and t.DM_IN_RECYCLE = #{dmInRecycle}



		and t.KM_MANAGER_ID = #{kmManagerId}



		and t.KM_SORT = #{kmSort}



		and t.KM_DEADLINE = #{kmDeadline}



		and t.WEIGHT = #{weight}



		and t.KM_STANDARD_COUNT = #{kmStandardCount}



		and t.KM_APP_FLAG = #{kmAppFlag}



		and t.KM_STANDARD_TIME = #{kmStandardTime}



		and t.PERMISSION_VALUE = #{permissionValue}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}



	order by ${orderBy}

SELECT count(ID)
FROM toa_archive_folder t 


		and t.ID = #{id}



		and t.FOLDER_TYPE = #{folderType}



		and t.NODE_TYPE = #{nodeType}



		and t.PARENT_FOLDER_ID = #{parentFolderId}



		and t.FOLDER_NAME = #{folderName}



		and t.FOLDER_PATH = #{folderPath}



		and t.DM_IN_RECYCLE = #{dmInRecycle}



		and t.KM_MANAGER_ID = #{kmManagerId}



		and t.KM_SORT = #{kmSort}



		and t.KM_DEADLINE = #{kmDeadline}



		and t.WEIGHT = #{weight}



		and t.KM_STANDARD_COUNT = #{kmStandardCount}



		and t.KM_APP_FLAG = #{kmAppFlag}



		and t.KM_STANDARD_TIME = #{kmStandardTime}



		and t.PERMISSION_VALUE = #{permissionValue}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

mapper.delete:
delete from toa_archive_folder
where ID in

	#{primaryKey}

mapper.update:
UPDATE toa_archive_folder t set t.delete_flag = 1,t.modify_user =
#{modifyUser},t.modify_date = #{modifyDate}
where ID in

	#{primaryKey}

UPDATE toa_archive_folder t set t.DM_IN_RECYCLE = 1,t.modify_user =
#{modifyUser},t.modify_date = #{modifyDate}
where ID in

	#{primaryKey}

mapper.select:
SELECT a.id id, a.FOLDER_TYPE folderType, a.NODE_TYPE nodeType, a.PARENT_FOLDER_ID parentFolderId, a.FOLDER_NAME folderName,
a.DM_IN_RECYCLE dmInRecycle, a.KM_MANAGER_ID kmManagerId, a.KM_SORT kmSort, a.KM_DEADLINE kmDeadline, a.WEIGHT weight, a.KM_STANDARD_COUNT kmStandardCount, a.KM_APP_FLAG kmAppFlag, a.KM_STANDARD_TIME kmStandardTime, a.PERMISSION_VALUE permissionValue, a.DELETE_FLAG deleteFlag, a.CREATE_USER createUser, a.CREATE_USER_DEPT createUserDept, a.CREATE_USER_ORG createUserOrg, a.CREATE_DATE createDate, a.MODIFY_USER modifyUser, a.MODIFY_DATE modifyDate, u.DISPLAY_NAME owner FROM toa_archive_folder a LEFT JOIN toa_sys_user u on a.create_user =u.ID
WHERE a.id in ( select b.folder_id from toa_archive_permission b, toa_archive_sub_permission c where b.id = c.permission_id AND ( ( c.control_id = #{createUser} AND c.data_type = '1' ) OR ( c.control_id = #{createUserDept} AND c.data_type = '2' ) OR ( c.control_id = #{createUserOrg} AND c.data_type = '2' ) ) AND b.PERMISSION_VALUE = '1111000000' ) AND a.folder_type = '4'
AND a.DELETE_FLAG = 0 AND a.DM_IN_RECYCLE = 0 AND a.FOLDER_NAME != '根目录' AND a.PARENT_FOLDER_ID = #{parentFolderId} AND a.DELETE_FLAG=0 AND a.CREATE_USER_ORG = #{createUserOrg}
order by ${orderBy}

mapper.update:
UPDATE toa_archive_folder set
FOLDER_PATH =REPLACE(FOLDER_PATH,#{oldFolderPath}, #{folderPath})
where FOLDER_PATH like '${oldFolderPath}%' and Folder_type=#{folderType}
