mapper.select:
SELECT 
	t.ID id,
	t.TOA_ID toaId,
	t.DOCUMENT_ID documentId,
	t.KM_USER_ID kmUserId,
	t.KM_READTIME kmReadtime,
	t.KM_FIRSTREAD kmFirstread,
	t.KM_LASTREAD kmLastread,
	t.KM_READ_FLAG kmReadFlag,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.WEIGHT weight,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM toa_archive_readinfo t


		and t.ID = #{id}



		and t.TOA_ID = #{toaId}



		and t.DOCUMENT_ID = #{documentId}



		and t.KM_USER_ID = #{kmUserId}



		and t.KM_READTIME = #{kmReadtime}


		and t.KM_READTIME >= #{kmReadtimeBegin}


		and t.KM_READTIME <= #{kmReadtimeEnd}



		and t.KM_FIRSTREAD = #{kmFirstread}


		and t.KM_FIRSTREAD >= #{kmFirstreadBegin}


		and t.KM_FIRSTREAD <= #{kmFirstreadEnd}



		and t.KM_LASTREAD = #{kmLastread}


		and t.KM_LASTREAD >= #{kmLastreadBegin}


		and t.KM_LASTREAD <= #{kmLastreadEnd}



		and t.KM_READ_FLAG = #{kmReadFlag}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.WEIGHT = #{weight}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

SELECT count(ID) 
FROM toa_archive_readinfo t


		and t.ID = #{id}



		and t.TOA_ID = #{toaId}



		and t.DOCUMENT_ID = #{documentId}



		and t.KM_USER_ID = #{kmUserId}



		and t.KM_READTIME = #{kmReadtime}


		and t.KM_READTIME >= #{kmReadtimeBegin}


		and t.KM_READTIME <= #{kmReadtimeEnd}



		and t.KM_FIRSTREAD = #{kmFirstread}


		and t.KM_FIRSTREAD >= #{kmFirstreadBegin}


		and t.KM_FIRSTREAD <= #{kmFirstreadEnd}



		and t.KM_LASTREAD = #{kmLastread}


		and t.KM_LASTREAD >= #{kmLastreadBegin}


		and t.KM_LASTREAD <= #{kmLastreadEnd}



		and t.KM_READ_FLAG = #{kmReadFlag}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.WEIGHT = #{weight}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from toa_archive_readinfo
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_archive_readinfo t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
