oid setDepartment(String department) {	    this.department=department;	}	public String getSale() {	    return sale;	}	public void setSale(String sale) {	    this.sale=sale;	}	public String getTradeDepartment() {	    return tradeDepartment;	}	public void setTradeDepartment(String tradeDepartment) {	    this.tradeDepartment=tradeDepartment;	}	public String getPerEnSale() {	    return perEnSale;	}	public void setPerEnSale(String perEnSale) {	    this.perEnSale=perEnSale;	}	public BigDecimal getPerAccount() {	    return perAccount;	}	public void setPerAccount(BigDecimal perAccount) {	    this.perAccount=perAccount;	}	public BigDecimal getPerAgentAccount() {	    return perAgentAccount;	}	public void setPerAgentAccount(BigDecimal perAgentAccount) {	    this.perAgentAccount=perAgentAccount;	}	public BigDecimal getPerPureAccount() {	    return perPureAccount;	}	public void setPerPureAccount(BigDecimal perPureAccount) {	    this.perPureAccount=perPureAccount;	}	public BigDecimal getBillAccount() {	    return billAccount;	}	public void setBillAccount(BigDecimal billAccount) {	    this.billAccount=billAccount;	}	public BigDecimal getAddBill() {	    return addBill;	}	public void setAddBill(BigDecimal addBill) {	    this.addBill=addBill;	}	public BigDecimal getKuorong() {	    return kuorong;	}	public void setKuorong(BigDecimal kuorong) {	    this.kuorong=kuorong;	}
