mapper.select:
		SELECT 
		 t.ID id
		,t.company_id companyId
		,t.company_name companyName
		,t.gbps gbps
		,t.line line
		,t.wanglin wanglin
		,t.yzl yzl
		,t.langfang langfang
		,t.lugu lugu
		,t.dongsi dongsi
		,t.huasi huasi
		,t.shandong shandong
		,t.yidong yidong
		,t.tietong tietong
		,t.jiaoyu jiaoyu
		,t.keji keji
		,t.cnisp cnisp
		,t.guoji guoji
		,t.transfer transfer
		,t.remark remark
		,t.DELETE_FLAG deleteFlag
		,t.CREATE_USER createUser
		,t.CREATE_USER_DEPT createUserDept
		,t.CREATE_DATE createDate
		,t.MODIFY_USER modifyUser
		,t.MODIFY_DATE modifyDate
		,t.EXT_STR1 extStr1
		,t.EXT_STR2 extStr2
		,t.EXT_STR3 extStr3
		,t.EXT_STR4 extStr4
		,t.EXT_STR5 extStr5
		,t.EXT_DATE1 extDate1
		,t.EXT_DATE2 extDate2
		,t.EXT_NUM1 extNum1
		,t.EXT_NUM2 extNum2
		,t.EXT_NUM3 extNum3
 FROM toa_network_bandwidth t


		and t.id=#{id}


		and t.company_id=#{companyId}


		and t.company_name like '%${companyName}%'


		and t.gbps=#{gbps}


		and t.line=#{line}


		and t.wanglin=#{wanglin}


		and t.yzl=#{yzl}


		and t.langfang=#{langfang}


		and t.lugu=#{lugu}


		and t.dongsi=#{dongsi}


