mapper.select:
SELECT 
	t.ID id,
	t.TASK_ID taskId,
	t.TASK_EVENT_TYPE taskEventType,
	t.TASK_EVENT_TITLE taskEventTitle,
	t.CONTENT content,
	t.REPORT_CONTENT reportContent,
	t.REPORT_PROC reportProc,
	t.DELAY_REASON delayReason,
	t.DELAY_ENDDATE delayEnddate,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM tty_po_task_history t


		and t.ID = #{id}


		and t.TASK_ID = #{taskId}


		and t.TASK_EVENT_TYPE = #{taskEventType}


		and t.TASK_EVENT_TITLE = #{taskEventTitle}


		and t.CONTENT = #{content}


		and t.REPORT_CONTENT = #{reportContent}


		and t.REPORT_PROC = #{reportProc}


		and t.DELAY_REASON = #{delayReason}


		and t.DELAY_ENDDATE = #{delayEnddate}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}


ORDER BY t.CREATE_DATE DESC

SELECT count(ID) 
FROM tty_po_task_history t


		and t.ID = #{id}


		and t.TASK_ID = #{taskId}


		and t.TASK_EVENT_TYPE = #{taskEventType}


		and t.TASK_EVENT_TITLE = #{taskEventTitle}


		and t.CONTENT = #{content}


		and t.REPORT_CONTENT = #{reportContent}


		and t.REPORT_PROC = #{reportProc}


		and t.DELAY_REASON = #{delayReason}


		and t.DELAY_ENDDATE = #{delayEnddate}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from tty_po_task_history
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_po_task_history t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.select:
SELECT  DISTINCT th.TASK_ID taskId
 FROM tty_po_task_history th 
WHERE  th.task_id IN (
SELECT id FROM tty_po_task tp

	tp.DELETE_FLAG=0 and tp.STATUS NOT IN (3,4)

		and tp.DIRECTOR_ID = #{directorId}


		and tp.START_TIME between  #{startTime} and #{endTime}


		and tp.START_TIME >=  #{startTime}


		and tp.START_TIME <=  #{endTime}


) AND  th.DELETE_FLAG=0 AND th.TASK_EVENT_TYPE=6

SELECT COUNT(DISTINCT th.task_id) FROM tty_po_task_history th 
WHERE  th.task_id IN (
SELECT id FROM tty_po_task tp

	tp.DELETE_FLAG=0 and tp.STATUS NOT IN (3,4)

		and tp.DIRECTOR_ID = #{directorId}


		and tp.START_TIME between  #{startTime} and #{endTime}


		and tp.START_TIME >=  #{startTime}


		and tp.START_TIME <=  #{endTime}


) AND  th.DELETE_FLAG=0 AND th.TASK_EVENT_TYPE=6

SELECT @rownum:=@rownum+1 rowNum, t.ID id, t.TASK_ID taskId, t.TASK_EVENT_TYPE taskEventType, t.TASK_EVENT_TITLE taskEventTitle, t.CONTENT content, t.REPORT_CONTENT reportContent, t.REPORT_PROC reportProc, t.DELAY_REASON delayReason, t.DELAY_ENDDATE delayEnddate, t.DELETE_FLAG deleteFlag, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_USER_ORG createUserOrg, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.EXT_DATE1 extDate1, t.EXT_DATE2 extDate2, t.EXT_NUM1 extNum1, t.EXT_NUM2 extNum2, t.EXT_NUM3 extNum3, t.EXT_STR1 extStr1, t.EXT_STR2 extStr2, t.EXT_STR3 extStr3, t.EXT_STR4 extStr4, t.EXT_STR5 extStr5, (SELECT ts.display_name FROM toa_sys_user ts WHERE ts.ID=t.CREATE_USER) userName
FROM (SELECT @rownum:=0) r,tty_po_task_history t
and t.TASK_ID = #{taskId}
and t.TASK_EVENT_TYPE = #{taskEventType}
and t.DELETE_FLAG = #{deleteFlag}

SELECT count(ID) 
FROM tty_po_task_history t


		and t.TASK_ID = #{taskId}


		and t.TASK_EVENT_TYPE = #{taskEventType}


		and t.DELETE_FLAG = #{deleteFlag}
