mapper.select:
SELECT 
	t.ID id,
	t.TASK_ID taskId,
	t.TASK_PERSON_ID taskPersonId,
	t.IS_PARENT isParent,
	t.TASK_TYPE taskType,
	t.DELETE_FLAG deleteFlag,
	t.EXT_DATE1 extDate1,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2
FROM tty_po_task_relevance t


		and t.ID = #{id}


		and t.TASK_ID = #{taskId}


		and t.TASK_PERSON_ID = #{taskPersonId}


		and t.IS_PARENT = #{isParent}


		and t.TASK_TYPE = #{taskType}


		and t.DELETE_FLAG = #{deleteFlag}

SELECT count(ID) 
FROM tty_po_task_relevance t


		and t.ID = #{id}


		and t.TASK_ID = #{taskId}


		and t.TASK_PERSON_ID = #{taskPersonId}


		and t.IS_PARENT = #{isParent}


		and t.TASK_TYPE = #{taskType}


		and t.DELETE_FLAG = #{deleteFlag}

mapper.delete:
    	delete from tty_po_task_relevance
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_po_task_relevance t set t.delete_flag = 1 
      		where ID in

	            #{primaryKey}
