package com.jc.shjfgl.machine.domain;

import com.jc.foundation.domain.BaseBean;
import com.jc.system.dic.IDicManager;
import com.jc.system.dic.impl.DicManagerImpl;

import java.util.Date;
/**
 * @author mrb
 * @version 设备上下架和迁移
*/
public class ToaEquipmentUpDown extends BaseBean {
	
	private static final long serialVersionUID = 1L;
	private String equipmentNumber;//   工单编号	private Integer companyId;//   客户ID	private String companyName;//   客户名称	private String contact;//   客户联系人	private String tel;//   客户联系方式	private Date billDate;//   下单日期	private Date startDate;//   操作开始时间	private Date endDate;//   操作完成时间	private String equipmentId;//   设备ID	private String address;//   上架-设备放置位置	private String lineAccess;//   上架-网线连接要求	private String lineUseType;//   上架-网线使用类型	private String isInstallOperation;//   上架-是否安装系统	private String isConfigIp;//   上架-是否配置IP	private String isSpaceSatisfied;//   上架-机柜空间是否满足	private String isElectricitySatisfied;//   上架-机柜电力是否满足	private String realLineAccess;//   上架-网线实际连接情况	private Date intDate;//   下架-操作时间	private String isRemoteClose;//   下架-是否远程关机	private String afterDown;//   下架-下架后要求	private String equipmentCheckResult;//   下架-设备信息核对结果	private String closeResult;//   下架-关机操作结果	private String downEquipmentList;//   下架-下架设备清单	private String haveAfterOperate;//   是否存在后续操作	private String operationType;//   工单类型（3.设备上架 4、设备下架）	private String operationTypeImg;//   工单类型图标名称（3.设备上架 4、设备下架）	private String machina;//   机房	private String caozgcs;//   操作人员ID	private String caozgcsName;//   操作人员姓名	private Integer status;//   处理状态	private String remark;//   备注
	public String getEquipmentNumber() {	    return equipmentNumber;	}	public void setEquipmentNumber(String equipmentNumber) {	    this.equipmentNumber=equipmentNumber;	}	public Integer getCompanyId() {	    return companyId;	}	public void setCompanyId(Integer companyId) {	    this.companyId=companyId;	}	public String getCompanyName() {	    return companyName;	}	public void setCompanyName(String companyName) {	    this.companyName=companyName;	}	public String getContact() {	    return contact;	}	public void setContact(String contact) {	    this.contact=contact;	}	public String getTel() {	    return tel;	}	public void setTel(String tel) {	    this.tel=tel;	}	public Date getBillDate() {	    return billDate;	}	public void setBillDate(Date billDate) {	    this.billDate=billDate;	}	public Date getStartDate() {	    return startDate;	}	public void setStartDate(Date startDate) {	    this.startDate=startDate;	}	public Date getEndDate() {	    return endDate;	}	public void setEndDate(Date endDate) {	    this.endDate=endDate;	}	public String getEquipmentId() {	    return equipmentId;	}	public void setEquipmentId(String equipmentId) {	    this.equipmentId=equipmentId;	}	public String getAddress() {	    return address;	}	public void setAddress(String address) {	    this.address=address;	}	public String getLineAccess() {	    return lineAccess;	}	public void setLineAccess(String lineAccess) {	    this.lineAccess=lineAccess;	}	public String getLineUseType() {	    return lineUseType;	}	public void setLineUseType(String lineUseType) {	    this.lineUseType=lineUseType;	}	public String getIsInstallOperation() {	    return isInstallOperation;	}	public void setIsInstallOperation(String isInstallOperation) {	    this.isInstallOperation=isInstallOperation;	}	public String getIsConfigIp() {	    return isConfigIp;	}	public void setIsConfigIp(String isConfigIp) {	    this.isConfigIp=isConfigIp;	}	public String getIsSpaceSatisfied() {	    return isSpaceSatisfied;	}	public void setIsSpaceSatisfied(String isSpaceSatisfied) {	    this.isSpaceSatisfied=isSpaceSatisfied;	}	public String getIsElectricitySatisfied() {	    return isElectricitySatisfied;	}	public void setIsElectricitySatisfied(String isElectricitySatisfied) {	    this.isElectricitySatisfied=isElectricitySatisfied;	}	public String getRealLineAccess() {	    return realLineAccess;	}	public void setRealLineAccess(String realLineAccess) {	    this.realLineAccess=realLineAccess;	}	public Date getIntDate() {	    return intDate;	}	public void setIntDate(Date intDate) {	    this.intDate=intDate;	}	public String getIsRemoteClose() {	    return isRemoteClose;	}	public void setIsRemoteClose(String isRemoteClose) {	    this.isRemoteClose=isRemoteClose;	}	public String getAfterDown() {	    return afterDown;	}	public void setAfterDown(String afterDown) {	    this.afterDown=afterDown;	}	public String getEquipmentCheckResult() {	    return equipmentCheckResult;	}	public void setEquipmentCheckResult(String equipmentCheckResult) {	    this.equipmentCheckResult=equipmentCheckResult;	}	public String getCloseResult() {	    return closeResult;	}	public void setCloseResult(String closeResult) {	    this.closeResult=closeResult;	}	public String getDownEquipmentList() {	    return downEquipmentList;	}	public void setDownEquipmentList(String downEquipmentList) {	    this.downEquipmentList=downEquipmentList;	}	public String getHaveAfterOperate() {	    return haveAfterOperate;	}	public void setHaveAfterOperate(String haveAfterOperate) {	    this.haveAfterOperate=haveAfterOperate;	}	public String getOperationType() {	    return operationType;	}	public void setOperationType(String operationType) {	    this.operationType=operationType;	}
	public String getOperationTypeValue() {
		IDicManager dicManager = new DicManagerImpl();
		return dicManager.getDic("operateType", this.getOperationType()) == null ? "" : dicManager.getDic("operateType", this.getOperationType()).getValue();
	}	public String getOperationTypeImg() {	    return operationTypeImg;	}	public void setOperationTypeImg(String operationTypeImg) {	    this.operationTypeImg=operationTypeImg;	}	public String getMachina() {	    return machina;	}	public void setMachina(String machina) {	    this.machina=machina;	}
	public String getMachinaValue() {
		IDicManager dicManager = new DicManagerImpl();
		return dicManager.getDic("room", this.getMachina()) == null ? "" : dicManager.getDic("room", this.getMachina()).getValue();
	}	public String getCaozgcs() {	    return caozgcs;	}	public void setCaozgcs(String caozgcs) {	    this.caozgcs=caozgcs;	}	public String getCaozgcsName() {	    return caozgcsName;	}	public void setCaozgcsName(String caozgcsName) {	    this.caozgcsName=caozgcsName;	}	public Integer getStatus() {	    return status;	}	public void setStatus(Integer status) {	    this.status=status;	}	public String getRemark() {	    return remark;	}	public void setRemark(String remark) {	    this.remark=remark;	}}

