mapper.select:
SELECT 
	t.ID id,
	t.NAME name,
	t.PARENT_ID parentId,
	t.ICON icon,
	t.MENU_TYPE menuType,
	t.ACTION_NAME actionName,
	t.DELETE_FLAG deleteFlag,
	t.QUEUE queue,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.IS_SHOW isShow,
	t.permission permission

FROM toa_sys_menu t


		and t.ID = #{id}


		and t.NAME = #{name}


		and t.PARENT_ID = #{parentId}


		and t.ICON = #{icon}


		and t.MENU_TYPE = #{menuType}


		and t.ACTION_NAME = #{actionName}

		and t.DELETE_FLAG = 0

		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.IS_SHOW = #{isShow}


order by t.QUEUE,t.id

SELECT count(ID) 
FROM toa_sys_menu t


		and t.ID = #{id}


		and t.NAME = #{name}


		and t.PARENT_ID = #{parentId}


		and t.ICON = #{icon}


		and t.MENU_TYPE = #{menuType}


		and t.ACTION_NAME = #{actionName}

		and t.DELETE_FLAG = 0

		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.IS_SHOW = #{isShow}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT t.ID id, t.NAME name, t.PARENT_ID parentId, t.ICON icon, t.MENU_TYPE menuType, t.ACTION_NAME actionName, t.DELETE_FLAG deleteFlag, t.QUEUE queue, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.IS_SHOW isShow, t.permission permission
FROM toa_sys_menu as t
1=1
and ID IN ( SELECT MENU_ID FROM toa_sys_role_menu a INNER JOIN toa_sys_user_role b ON a.ROLE_ID = b.ROLE_ID WHERE b.USER_ID = #{userId}
)
and t.PARENT_ID = #{parentId}
AND t.IS_SHOW = 0 AND t.DELETE_FLAG = 0 ORDER BY t.QUEUE,t.id

mapper.delete:
    	delete from toa_sys_menu
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_sys_menu t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.select:
SELECT t.ID id, t.NAME name, t.PARENT_ID parentId, t.ICON icon, t.MENU_TYPE menuType, t.ACTION_NAME actionName, t.DELETE_FLAG deleteFlag, t.QUEUE queue, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.IS_SHOW isShow, t.permission permission FROM toa_sys_menu t,toa_sys_role_menu r, toa_sys_user_role u
t.ID = r.MENU_ID and r.ROLE_ID = u.ROLE_ID
and t.ID = #{id}
and u.USER_ID = #{userId}
