mapper.select:
SELECT t.ID as id , t.NAME as name , t.dept_id as deptId, t1.name as deptName, t.DESCRIPTION as description , t.DELETE_FLAG as deleteFlag , t.CREATE_USER as createUser , t.CREATE_DATE as createDate, t.CREATE_USER_DEPT createUserDept, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, COUNT(t2.ROLE_ID) personNum FROM toa_sys_role t left join tty_sys_department t1 on t.dept_id = t1.id AND t1.DELETE_FLAG = 0 LEFT JOIN toa_sys_user_role t2 ON t.ID = t2.ROLE_ID AND t2.DELETE_FLAG = 0 WHERE 1 = 1
and t.ID = #{id}
and t.NAME = #{name}
and t.dept_id in (${deptIds})
and t.DESCRIPTION = #{description}
and t.DELETE_FLAG = #{deleteFlag}
and t.CREATE_USER = #{createUser}
and t.CREATE_DATA = #{createDate}
GROUP BY id,deptId,description,createUser,createUserDept,modifyDate
order by ${orderBy}

SELECT count(t.ID) 
FROM 
		toa_sys_role t


		and t.ID = #{id}


		and t.NAME = #{name}


		and t.DEPT_ID in (${deptIds})


		and t.DESCRIPTION = #{description}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATA = #{createDate}

mapper.update:

    		UPDATE toa_sys_role set DELETE_FLAG = 1,  MODIFY_USER = #{modifyUser}, MODIFY_DATE = #{modifyDate}

      		where ID in

	            #{primaryKey}

mapper.select:
SELECT
		t.ID as id ,
		t.NAME as name ,
		t.dept_id as deptId

FROM 
		toa_sys_role t  WHERE t.DELETE_FLAG = 0


		and t.ID = #{id}


		and t.NAME = #{name}


		and t.dept_id in (${deptIds})



		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATA = #{createDate}



		order by ${orderBy}
