mapper.select:
SELECT 
	t.ID id,
	t.USER_ID userId,
	t.BIND_TYPE bindType,
	t.USER_START_IP userStartIp,
	t.USER_END_IP userEndIp,
	t.USER_IP_DESC userIpDesc,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	u.display_name userName,
	d.`NAME` deptName

FROM toa_sys_user_ip t, toa_sys_user u, tty_sys_department d

	and t.USER_ID = u.ID and u.DEPT_ID = d.ID 

		and t.ID = #{id}


		and t.USER_ID = #{userId}


		and t.BIND_TYPE = #{bindType}


		and t.USER_START_IP = #{userStartIp}


		and t.USER_END_IP = #{userEndIp}


		and t.USER_IP_DESC = #{userIpDesc}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and d.NAME like '%${deptName}%'


		order by ${orderBy}

SELECT count(t.ID) 
FROM toa_sys_user_ip t, toa_sys_user u, tty_sys_department d

	and t.USER_ID = u.ID and u.DEPT_ID = d.ID 

		and t.ID = #{id}


		and t.USER_ID = #{userId}


		and t.BIND_TYPE = #{bindType}


		and t.USER_START_IP = #{userStartIp}


		and t.USER_END_IP = #{userEndIp}


		and t.USER_IP_DESC = #{userIpDesc}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and d.NAME like '%${deptName}%'

mapper.delete:
    	delete from toa_sys_user_ip
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_sys_user_ip t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
