mapper.select:
SELECT
u.DISPLAY_NAME displayName,
t.ID id,
t.CODE code,
t.NAME name,
t.DEPT_DESC deptDesc,
t.LEADER_ID leaderId,
t.CHARGE_LEADER_ID chargeLeaderId,
t.PARENT_ID parentId,
t.MANAGER_DEPT managerDept,
t.DEPT_TYPE deptType,
t.ORGANIZATION_ID organizationId,
t.QUEUE queue,
t.CREATE_USER createUser,
t.CREATE_DATE createDate,
t.CREATE_USER_DEP createUserDept,
t.MODIFY_USER modifyUser,
t.MODIFY_DATE modifyDate,
t.DELETE_FLAG deleteFlag,

t.ADMIN_AREA adminArea,
t.ALL_NAME allName,
t.SHOW_NAME showName,
t.ORG_TYPE orgType

FROM tty_sys_department t left join toa_sys_user u ON t.LEADER_ID = u.id


		and t.ID = #{id}


		and t.CODE = #{code}


		and t.NAME = #{name}


		and t.DEPT_DESC = #{deptDesc}


		and t.LEADER_ID = #{leaderId}


		and t.CHARGE_LEADER_ID = #{chargeLeaderId}


		and t.PARENT_ID = #{parentId}


		and t.MANAGER_DEPT = #{managerDept}


		and t.DEPT_TYPE = #{deptType}


		and t.ORGANIZATION_ID = #{organizationId}


		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATE = #{createDate}


		and t.CREATE_USER_DEP = #{createUserDept}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.DELETE_FLAG = #{deleteFlag}


order by t.QUEUE, t.ID

SELECT count(ID)
FROM tty_sys_department t


		and t.ID = #{id}


		and t.CODE = #{code}


		and t.NAME = #{name}


		and t.DEPT_DESC = #{deptDesc}


		and t.LEADER_ID = #{leaderId}


		and t.CHARGE_LEADER_ID = #{chargeLeaderId}


		and t.PARENT_ID = #{parentId}


		and t.MANAGER_DEPT = #{managerDept}


		and t.DEPT_TYPE = #{deptType}


		and t.ORGANIZATION_ID = #{organizationId}


		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATE = #{createDate}


		and t.CREATE_USER_DEP = #{createUserDept}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.DELETE_FLAG = #{deleteFlag}

mapper.delete:
delete from tty_sys_department
where ID in

	#{primaryKey}

mapper.select:
SELECT
u.DISPLAY_NAME displayName,
t.ID id,
t.CODE code,
t.NAME name,
t.DEPT_DESC deptDesc,
t.LEADER_ID leaderId,
t.CHARGE_LEADER_ID chargeLeaderId,
t.PARENT_ID parentId,
t.MANAGER_DEPT managerDept,
t.DEPT_TYPE deptType,
t.ORGANIZATION_ID organizationId,
t.QUEUE queue,
t.CREATE_USER createUser,
t.CREATE_DATE createDate,
t.CREATE_USER_DEP createUserDept,
t.MODIFY_USER modifyUser,
t.MODIFY_DATE modifyDate,
t.DELETE_FLAG deleteFlag,

t.ADMIN_AREA adminArea,
t.ALL_NAME allName,
t.SHOW_NAME showName,
t.ORG_TYPE orgType

FROM tty_sys_department t left join toa_sys_user u ON
t.LEADER_ID = u.id


		and t.ID = #{id}


		and t.CODE = #{code}


		and t.NAME = #{name}


		and t.DEPT_DESC = #{deptDesc}


		and t.LEADER_ID = #{leaderId}


		and t.CHARGE_LEADER_ID = #{chargeLeaderId}


		and t.PARENT_ID = #{parentId}


		and t.MANAGER_DEPT = #{managerDept}


		and t.DEPT_TYPE = #{deptType}


		and t.ORGANIZATION_ID = #{organizationId}


		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATE = #{createDate}


		and t.CREATE_USER_DEP = #{createUserDept}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.DELETE_FLAG = #{deleteFlag}


order by t.QUEUE, t.ID

SELECT count(t.ID)
FROM tty_sys_department t left join toa_sys_user u
on t.LEADER_ID=u.id



		and t.PARENT_ID = #{id}


		and t.CODE = #{code}


		and t.NAME = #{name}


		and t.DEPT_DESC = #{deptDesc}


		and t.LEADER_ID = #{leaderId}


		and t.CHARGE_LEADER_ID = #{chargeLeaderId}


		and t.PARENT_ID = #{parentId}


		and t.MANAGER_DEPT = #{managerDept}


		and t.DEPT_TYPE = #{deptType}


		and t.ORGANIZATION_ID = #{organizationId}


		and t.QUEUE = #{queue}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_DATE = #{createDate}


		and t.CREATE_USER_DEP = #{createUserDept}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.id in (${deptIds})

