mapper.select:
	SELECT 
		t.id id,
		t.code code,
		t.dic_type dicType,
		t.value value,
		t.parent_id parentId,
		t.parent_type parentType,
		t.use_flag useFlag,
		t.type_flag typeFlag,
		t.dic_flag dicFlag,
		t.default_value defaultValue,
		t.order_flag orderFlag
	FROM tty_sys_dics t


			and t.id = #{id}


			and t.code = #{code}


   			and t.dic_type = #{dicType}


			and t.value = #{value}


			and t.parent_id = #{parentId}


   			and t.parent_type = #{parentType}


			and t.use_flag = #{useFlag}


			and t.type_flag = #{typeFlag}


			and t.dic_flag = #{dicFlag}


			and t.default_value = #{defaultValue}


			and t.DELETE_FLAG = #{deleteFlag}


	order by t.id asc

	SELECT count(id) 
	FROM tty_sys_dics t


			and t.id = #{id}


			and t.code = #{code}


   			and t.dic_type = #{dicType}


			and t.value = #{value}


			and t.parent_id = #{parentId}


   			and t.parent_type = #{parentType}


			and t.use_flag = #{useFlag}


			and t.type_flag = #{typeFlag}


			and t.dic_flag = #{dicFlag}


			and t.default_value = #{defaultValue}


			and t.DELETE_FLAG = #{deleteFlag}

mapper.delete:
    	delete from tty_sys_dics


