mapper.select:
SELECT t.id id, t.name name, t.group_type groupType, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_USER_ORG createUserOrg, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.DELETE_FLAG deleteFlag, t.EXT_DATE1 extDate1, t.EXT_DATE2 extDate2, t.EXT_NUM1 extNum1, t.EXT_NUM2 extNum2, t.EXT_NUM3 extNum3, t.EXT_STR1 extStr1, t.EXT_STR2 extStr2, t.EXT_STR3 extStr3, t.EXT_STR4 extStr4, t.EXT_STR5 extStr5
FROM toa_sys_group t
and t.id = #{id}
and t.name like '%${name}%'
and t.group_type = #{groupType}
and t.CREATE_USER = #{createUser}
and t.CREATE_USER_DEPT = #{createUserDept}
and t.CREATE_USER_ORG = #{createUserOrg}

	SELECT count(id) 
	FROM toa_sys_group t


			and t.id = #{id}


			and t.name like '%${name}%'


   			and t.group_type = #{groupType}


   			and t.CREATE_USER = #{createUser}


   			and t.CREATE_USER_DEPT = #{createUserDept}


   			and t.CREATE_USER_ORG = #{createUserOrg}

	SELECT t.id, t.name 
	FROM toa_sys_group t


			and t.name = #{name}


   			and t.group_type = #{groupType}


   			and t.CREATE_USER = #{createUser}


   			and t.CREATE_USER_DEPT = #{createUserDept}


   			and t.CREATE_USER_ORG = #{createUserOrg}

