mapper.delete:
    	delete from tty_ic_mailbox
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_ic_mailbox t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

