mapper.select:
SELECT 
	t.ID id,
	t.FUN_NAME funName,
	t.FUN_CODE funCode,
	t.FUN_URL funUrl,
	t.LINE_TYPE lineType,
	t.VIEW_TYPE viewType,
	t.FUN_COLUMN_SHOW_NAME funColumnShowName,
	t.FUN_COLUMN_NAME funColumnName,
	t.FUN_URLPARAMETER funUrlparameter,
	t.FUN_URLMORE funUrlmore,
	t.FUN_PARAMETERTYPE funParametertype,
	t.FUN_ROWS funRows,
	t.FUN_URL_PARAMETERNAME funUrlParametername,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM tty_sys_portal_function t


		and t.ID = #{id}



		and t.FUN_NAME like '%${funName}%'



		and t.FUN_CODE like '%${funCode}%'



		and t.FUN_URL like '%${funUrl}%'



		and t.LINE_TYPE = #{lineType}



		and t.VIEW_TYPE = #{viewType}



		and t.FUN_COLUMN_SHOW_NAME = #{funColumnShowName}



		and t.FUN_COLUMN_NAME = #{funColumnName}



		and t.FUN_URLPARAMETER = #{funUrlparameter}



		and t.FUN_URLMORE = #{funUrlmore}



		and t.FUN_PARAMETERTYPE = #{funParametertype}



		and t.FUN_ROWS = #{funRows}



		and t.FUN_URL_PARAMETERNAME = #{funUrlParametername}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}


order by t.ID Desc

SELECT count(ID) 
FROM tty_sys_portal_function t


		and t.ID = #{id}



		and t.FUN_NAME like '%${funName}%'



		and t.FUN_CODE like '%${funCode}%'



		and t.FUN_URL like '%${funUrl}%'



		and t.LINE_TYPE = #{lineType}



		and t.VIEW_TYPE = #{viewType}



		and t.FUN_COLUMN_SHOW_NAME = #{funColumnShowName}



		and t.FUN_COLUMN_NAME = #{funColumnName}



		and t.FUN_URLPARAMETER = #{funUrlparameter}



		and t.FUN_URLMORE = #{funUrlmore}



		and t.FUN_PARAMETERTYPE = #{funParametertype}



		and t.FUN_ROWS = #{funRows}



		and t.FUN_URL_PARAMETERNAME = #{funUrlParametername}



		and t.DELETE_FLAG = #{deleteFlag}



		and t.CREATE_USER = #{createUser}



		and t.CREATE_USER_DEPT = #{createUserDept}



		and t.CREATE_USER_ORG = #{createUserOrg}



		and t.CREATE_DATE = #{createDate}


		and t.CREATE_DATE >= #{createDateBegin}


		and t.CREATE_DATE <= #{createDateEnd}



		and t.MODIFY_USER = #{modifyUser}



		and t.MODIFY_DATE = #{modifyDate}


		and t.MODIFY_DATE >= #{modifyDateBegin}


		and t.MODIFY_DATE <= #{modifyDateEnd}



		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE1 >= #{extDate1Begin}


		and t.EXT_DATE1 <= #{extDate1End}



		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_DATE2 >= #{extDate2Begin}


		and t.EXT_DATE2 <= #{extDate2End}



		and t.EXT_NUM1 = #{extNum1}



		and t.EXT_NUM2 = #{extNum2}



		and t.EXT_NUM3 = #{extNum3}



		and t.EXT_STR1 = #{extStr1}



		and t.EXT_STR2 = #{extStr2}



		and t.EXT_STR3 = #{extStr3}



		and t.EXT_STR4 = #{extStr4}



		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from tty_sys_portal_function
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_sys_portal_function t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.select:
	SELECT count(ID) 
	FROM tty_sys_portal_function t


   			and t.FUN_NAME = #{funName} 


   			and t.DELETE_FLAG = #{deleteFlag}
