mapper.select:
	SELECT 
		 t.ID id
		,t.COMPANY_NAME companyName
		,t.CUSTOMER_ID customerId
		,t.CUSTOMER_TYPE customerType
		,t.CONTRACT_NUMBER contractNumber
		,t.LEARD leard
		,t.LEARD_DATE leardDate
		,t.AGREEMENT agreement
		,t.CONTRACT_STATUS contractStatus
		,t.CONTRACT_MONEY contractMoney
		,t.SEAL seal
		,t.PLACE place
		,t.START_DATE startDate
		,t.END_DATE endDate
		,t.DERIVE derive
		,t.DERIVE_NO deriveNo
		,t.RESOURCE resource
		,t.CONTRACT_FILE contractFile
		,t.REMARK remark
		,t.DELETE_FLAG deleteFlag
		,t.CREATE_USER createUser
		,t.CREATE_USER_DEPT createUserDept
		,t.CREATE_DATE createDate
		,t.MODIFY_USER modifyUser
		,t.MODIFY_DATE modifyDate
		,t.EXT_STR1 extStr1
		,t.EXT_STR2 extStr2
		,t.EXT_STR3 extStr3
		,t.EXT_STR4 extStr4
		,t.EXT_STR5 extStr5
		,t.EXT_DATE1 extDate1
		,t.EXT_DATE2 extDate2
		,t.EXT_NUM1 extNum1
		,t.EXT_NUM2 extNum2
		,t.EXT_NUM3 extNum3
 FROM toa_contract_incontract t


		and t.id=#{id}


		and t.company_name like '%${companyName}%'


		and t.CUSTOMER_TYPE=#{customerType}


		and t.CUSTOMER_ID=#{customerId}


		and t.CONTRACT_NUMBER=#{contractNumber}


		and t.LEARD=#{leard}


		and t.LEARD_DATE=#{leardDate}


		and t.AGREEMENT=#{agreement}


		and t.CONTRACT_STATUS=#{contractStatus}


		and t.CONTRACT_MONEY=#{contractMoney}


		and t.SEAL=#{seal}


		and t.PLACE=#{place}


		and t.START_DATE=#{startDate}


		and t.END_DATE=#{endDate}



		and t.END_DATE >= #{endDateBegin}



		and t.END_DATE <= #{endDateEnd}


		and t.DERIVE=#{derive}


		and t.DERIVE_NO=#{deriveNo}


		and t.RESOURCE=#{resource}


		and t.CONTRACT_FILE=#{contractFile}


		and t.REMARK=#{remark}


		and t.DELETE_FLAG=#{deleteFlag}


		and t.CREATE_USER=#{createUser}


		and t.CREATE_USER_DEPT=#{createUserDept}


		and t.CREATE_DATE=#{createDate}


		and t.MODIFY_USER=#{modifyUser}


		and t.MODIFY_DATE=#{modifyDate}


		and t.EXT_STR1=#{extStr1}


		and t.EXT_STR2=#{extStr2}


		and t.EXT_STR3=#{extStr3}


		and t.EXT_STR4=#{extStr4}


		and t.EXT_STR5=#{extStr5}


		and t.EXT_DATE1=#{extDate1}


		and t.EXT_DATE2=#{extDate2}


		and t.EXT_NUM1=#{extNum1}


		and t.EXT_NUM2=#{extNum2}


		and t.EXT_NUM3=#{extNum3}



		order by ${orderBy}

SELECT COUNT(ID) 
 FROM toa_contract_incontract t


	and t.id=#{id}


	and t.company_name like '%${companyName}%'


	and t.CUSTOMER_TYPE=#{customerType}


	and t.CUSTOMER_ID=#{customerId}


	and t.CONTRACT_NUMBER=#{contractNumber}


	and t.LEARD=#{leard}


	and t.LEARD_DATE=#{leardDate}


	and t.AGREEMENT=#{agreement}


	and t.CONTRACT_STATUS=#{contractStatus}


	and t.CONTRACT_MONEY=#{contractMoney}


	and t.SEAL=#{seal}


	and t.PLACE=#{place}


	and t.START_DATE=#{startDate}


	and t.END_DATE=#{endDate}



	and t.END_DATE >= #{endDateBegin}


	and t.END_DATE <= #{endDateEnd}



	and t.DERIVE=#{derive}


	and t.DERIVE_NO=#{deriveNo}


	and t.RESOURCE=#{resource}


	and t.CONTRACT_FILE=#{contractFile}


	and t.REMARK=#{remark}


	and t.DELETE_FLAG=#{deleteFlag}


	and t.CREATE_USER=#{createUser}


	and t.CREATE_USER_DEPT=#{createUserDept}


	and t.CREATE_DATE=#{createDate}


	and t.MODIFY_USER=#{modifyUser}


	and t.MODIFY_DATE=#{modifyDate}


	and t.EXT_STR1=#{extStr1}


	and t.EXT_STR2=#{extStr2}


	and t.EXT_STR3=#{extStr3}


	and t.EXT_STR4=#{extStr4}


	and t.EXT_STR5=#{extStr5}


	and t.EXT_DATE1=#{extDate1}


	and t.EXT_DATE2=#{extDate2}


	and t.EXT_NUM1=#{extNum1}


	and t.EXT_NUM2=#{extNum2}


	and t.EXT_NUM3=#{extNum3}

mapper.update:
	     UPDATE toa_contract_incontract t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
 			where ID in

				 #{primaryKey}
