mapper.select:
		SELECT 
		 t.ID id
		,t.EQUIPMENT_NUMBER equipmentNumber
		,t.COMPANY_ID companyId
		,t.COMPANY_NAME companyName
		,t.MACHINA machina
		,t.CONTACT contact
		,t.TEL tel
		,t.INT_DATE intDate
		,t.TYPE type
		,t.COUNTT countt
		,t.DEVICE_INFO deviceInfo
		,t.INT_PEOPLE intPeople
		,t.IS_INPUT isInput
		,t.CAOZGCS caozgcs
		,t.ORIGIN origin
		,t.REMARK remark
		,t.STATUS status
		,t.DELETE_FLAG deleteFlag
		,t.CREATE_USER createUser
		,t.CREATE_USER_DEPT createUserDept
		,t.CREATE_DATE createDate
		,t.MODIFY_USER modifyUser
		,t.MODIFY_DATE modifyDate
		,t.EXT_STR1 extStr1
		,t.EXT_STR2 extStr2
		,t.EXT_STR3 extStr3
		,t.EXT_STR4 extStr4
		,t.EXT_STR5 extStr5
		,t.EXT_DATE1 extDate1
		,t.EXT_DATE2 extDate2
		,t.EXT_NUM1 extNum1
		,t.EXT_NUM2 extNum2
		,t.EXT_NUM3 extNum3
 FROM toa_machine_inout t


		and t.ID=#{id}


		and t.EQUIPMENT_NUMBER=#{equipmentNumber}


		and t.COMPANY_ID=#{companyId}


		and t.company_name like '%${companyName}%'


		and t.MACHINA=#{machina}


		and t.CONTACT=#{contact}


		and t.TEL=#{tel}


		and t.INT_DATE=#{intDate}


		and t.TYPE=#{type}


		and t.COUNTT=#{countt}


		and t.DEVICE_INFO=#{deviceInfo}


		and t.INT_PEOPLE=#{intPeople}


		and t.IS_INPUT=#{isInput}


		and t.CAOZGCS=#{caozgcs}


		and t.ORIGIN=#{origin}


		and t.REMARK=#{remark}


		and t.STATUS=#{status}


		and t.DELETE_FLAG=#{deleteFlag}


		and t.CREATE_USER=#{createUser}


		and t.CREATE_USER_DEPT=#{createUserDept}


		and t.CREATE_DATE=#{createDate}


		and t.MODIFY_USER=#{modifyUser}


		and t.MODIFY_DATE=#{modifyDate}


		and t.EXT_STR1=#{extStr1}


		and t.EXT_STR2=#{extStr2}


		and t.EXT_STR3=#{extStr3}


		and t.EXT_STR4=#{extStr4}


		and t.EXT_STR5=#{extStr5}


		and t.EXT_DATE1=#{extDate1}


		and t.EXT_DATE2=#{extDate2}


		and t.EXT_NUM1=#{extNum1}


		and t.EXT_NUM2=#{extNum2}


		and t.EXT_NUM3=#{extNum3}



			order by ${orderBy}

	SELECT COUNT(ID) 
 FROM toa_machine_inout t


	and t.ID=#{id}


	and t.EQUIPMENT_NUMBER=#{equipmentNumber}


	and t.COMPANY_ID=#{companyId}


	and t.company_name like '%${companyName}%'


	and t.MACHINA=#{machina}


	and t.CONTACT=#{contact}


	and t.TEL=#{tel}


	and t.INT_DATE=#{intDate}


	and t.TYPE=#{type}


	and t.COUNTT=#{countt}


	and t.DEVICE_INFO=#{deviceInfo}


	and t.INT_PEOPLE=#{intPeople}


	and t.IS_INPUT=#{isInput}


	and t.CAOZGCS=#{caozgcs}


	and t.ORIGIN=#{origin}


	and t.REMARK=#{remark}


	and t.STATUS=#{status}


	and t.DELETE_FLAG=#{deleteFlag}


	and t.CREATE_USER=#{createUser}


	and t.CREATE_USER_DEPT=#{createUserDept}


	and t.CREATE_DATE=#{createDate}


	and t.MODIFY_USER=#{modifyUser}


	and t.MODIFY_DATE=#{modifyDate}


	and t.EXT_STR1=#{extStr1}


	and t.EXT_STR2=#{extStr2}


	and t.EXT_STR3=#{extStr3}


	and t.EXT_STR4=#{extStr4}


	and t.EXT_STR5=#{extStr5}


	and t.EXT_DATE1=#{extDate1}


	and t.EXT_DATE2=#{extDate2}


	and t.EXT_NUM1=#{extNum1}


	and t.EXT_NUM2=#{extNum2}


	and t.EXT_NUM3=#{extNum3}

mapper.update:
	     UPDATE toa_machine_inout t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
 			where ID in

				 #{primaryKey}
