		,t.EQUIPMENT_CONSTRUCTION equipmentConstruction
		,t.MORMAL_EQUIPMENT mormalEquipment
		,t.HAVE_AFTER_OPERATE haveAfterOperate
		,t.REGION_INFORMATION regionInformation
		,t.OPERATION_TYPE operationType
		,t.OPERATION_TYPE_IMG operationTypeImg
