mapper.select:
SELECT 
	t.ID id,
	t.DATA_ID dataId,
	t.USER_ID userId,
	t.DEPT_ID deptId,
	t.CONTROL_TYPE controlType,
	t.PERMISSION_TYPE permissionType,
	t.TABLE_NAME tableName,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM toa_sys_control_side t


		and t.ID = #{id}


		and t.DATA_ID = #{dataId}


		and t.USER_ID = #{userId}


		and t.DEPT_ID = #{deptId}


		and t.CONTROL_TYPE = #{controlType}


		and t.PERMISSION_TYPE = #{permissionType}


		and t.TABLE_NAME = #{tableName}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT count(ID) 
FROM toa_sys_control_side t


		and t.ID = #{id}


		and t.DATA_ID = #{dataId}


		and t.USER_ID = #{userId}


		and t.DEPT_ID = #{deptId}


		and t.CONTROL_TYPE = #{controlType}


		and t.PERMISSION_TYPE = #{permissionType}


		and t.TABLE_NAME = #{tableName}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

mapper.delete:
delete from toa_sys_control_side
where ID in

	#{primaryKey}

mapper.update:
    	UPDATE toa_sys_control_side t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.delete:
    	delete from toa_sys_control_side


    		and DATA_ID = #{dataId} 


    		and DATA_ID in

	            #{user}



			and TABLE_NAME = #{tableName}


			and control_TYPE = #{controlType} 


			and CREATE_USER_ORG = #{createUserOrg}

    	delete from toa_sys_control_side


				and DATA_ID = #{dataId}


				and USER_ID = #{userId}


				and DEPT_ID = #{deptId}


				and CONTROL_TYPE = #{controlType}


				and PERMISSION_TYPE = #{permissionType}


				and TABLE_NAME = #{tableName}

delete from toa_sys_control_side
where ID in

	#{primaryKey}
