mapper.select:
SELECT 
	t.ID id,
	t.TYPE_NAME typeName,
	t.IS_FIXED isFixed,
	t.RECIPIENT_IDS recipientIds,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM toa_ic_suggest_type t


		and t.ID = #{id}


		and t.TYPE_NAME like '%${typeName}%'


		and t.IS_FIXED = #{isFixed}


		and t.RECIPIENT_IDS = #{recipientIds}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}



		order by ${orderBy}

SELECT count(ID) 
FROM toa_ic_suggest_type t


		and t.ID = #{id}


		and t.TYPE_NAME like '%${typeName}%'


		and t.IS_FIXED = #{isFixed}


		and t.RECIPIENT_IDS = #{recipientIds}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT 
	t.ID id,
	t.TYPE_NAME typeName,
	t.IS_FIXED isFixed,
	t.RECIPIENT_IDS recipientIds,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM toa_ic_suggest_type t


		and t.ID = #{id}


		and t.TYPE_NAME = #{typeName}


		and t.IS_FIXED = #{isFixed}


		and t.RECIPIENT_IDS = #{recipientIds}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}



		order by ${orderBy}

mapper.delete:
    	delete from toa_ic_suggest_type
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_ic_suggest_type t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
