mapper.select:
SELECT 
	t.ID id,
	t.USER_NAME userName,
	t.MAIL mail,
	t.CONTACTS_TYPE contactsType,
	t.SIMPLE_NAME simpleName,
	t.PHONE phone,
	t.SEX sex,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM tty_ic_contacts t


		and t.ID = #{id}


		and t.USER_NAME = #{userName}


		and t.MAIL = #{mail}


		and t.CONTACTS_TYPE = #{contactsType}


		and t.SIMPLE_NAME = #{simpleName}


		and t.PHONE = #{phone}


		and t.SEX = #{sex}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT count(ID) 
FROM tty_ic_contacts t


		and t.ID = #{id}


		and t.USER_NAME = #{userName}


		and t.MAIL = #{mail}


		and t.CONTACTS_TYPE = #{contactsType}


		and t.SIMPLE_NAME = #{simpleName}


		and t.PHONE = #{phone}


		and t.SEX = #{sex}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT 
	t.ID id,
	t.USER_NAME userName,
	t.MAIL mail,
	t.CONTACTS_TYPE contactsType,
	t.SIMPLE_NAME simpleName,
	t.PHONE phone,
	t.SEX sex,
	d.GROUP_NAME groupName,
	d.ID groupId,
	d.DESCRIPTION description,
    r.ID groupRId,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5
	FROM tty_ic_contacts t
	 LEFT JOIN tty_ic_con_group_r r ON t.ID = r.CONTACTS_ID 
	 LEFT JOIN tty_ic_contacts_group d ON d.ID = r.CONTACTS_GROUP_ID 

      t.DELETE_FLAG = 0

		and t.ID = #{id}


		and t.USER_NAME like '%${userName}%'


			and t.MAIL like '%${mail}%'


		and t.CONTACTS_TYPE = #{contactsType}


		and t.SIMPLE_NAME like '%${simpleName}%'


		and t.PHONE like '%${phone}%'


		and d.ID = #{groupId}


		and t.SEX = #{sex}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}



		order by ${orderBy}

SELECT count(t.ID) 
	FROM tty_ic_contacts t
	 LEFT JOIN tty_ic_con_group_r r ON t.ID = r.CONTACTS_ID 
	 LEFT JOIN tty_ic_contacts_group d ON d.ID = r.CONTACTS_GROUP_ID 

     t.DELETE_FLAG = 0

		and t.ID = #{id}


		and t.USER_NAME like '%${userName}%'


			and t.MAIL like '%${mail}%'


		and t.CONTACTS_TYPE = #{contactsType}


		and t.SIMPLE_NAME like '%${simpleName}%'


		and t.PHONE like '%${phone}%'


		and d.ID = #{groupId}


		and t.SEX = #{sex}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from tty_ic_contacts
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_ic_contacts t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.select:
SELECT 
	t.ID id,
	t.USER_NAME userName,
	t.MAIL mail,
	t.CONTACTS_TYPE contactsType,
	t.SIMPLE_NAME simpleName,
	t.PHONE phone,
	t.SEX sex,
	d.GROUP_NAME groupName,
	d.ID groupId,
	d.DESCRIPTION description,
    r.ID groupRId,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5
	FROM tty_ic_contacts t
	 LEFT JOIN tty_ic_con_group_r r ON t.ID = r.CONTACTS_ID 
	 LEFT JOIN tty_ic_contacts_group d ON d.ID = r.CONTACTS_GROUP_ID 

      t.DELETE_FLAG = 0

		and t.ID = #{id}


		and t.USER_NAME = #{userName}


		and t.MAIL = #{mail}


		and t.CONTACTS_TYPE = #{contactsType}


		and t.SIMPLE_NAME = #{simpleName}


		and t.PHONE = #{phone}


		and d.ID = #{groupId}


		and t.SEX = #{sex}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}


limit 0,1

SELECT CONCAT("-1",t1.id) id,0 groupId,group_name userName,'' mail,'' phone FROM tty_ic_contacts_group t1 WHERE t1.DELETE_FLAG = '0'
and t1.CREATE_USER = #{createUser}
UNION ALL SELECT -1 id,0 groupId,'未分组' userName,'' mail,'' phone UNION ALL SELECT t2.id,CASE WHEN CONCAT("-1",t3.contacts_group_id) IS NULL THEN -1 ELSE CONCAT("-1",t3.contacts_group_id) END groupId,user_name userName ,t2.mail mail,t2.phone phone FROM tty_ic_contacts t2 LEFT JOIN tty_ic_con_group_r t3 ON t2.id = t3.contacts_id WHERE t2.DELETE_FLAG = '0'
and t2.CREATE_USER = #{createUser}
