mapper.select:
SELECT
t.ID id,
t.CONTACTS_ID contactsId,
t.MAILBOX_ID mailboxId,
t.MAIL_TITLE mailTitle,
t.MAIL_CONTENT mailContent,
t.SENDER_USER_ID senderUserId,
t.SENDER_MAIL senderMail,
t.SEND_TYPE sendType,
t.ISFILE isfile,
t.SMS_ALERT smsAlert,
t.SIGNATURE signature,
t.PRESSING pressing,
t.ENCRYPTION encryption,
t.ENCRYPTION_PASS encryptionPass,
t.REPLY_TEXTING replyTexting,
t.REPLY_TEXTING_TIME replyTextingTime,
t.MAIL_STATUS mailStatus,
t.SENDER_TIME senderTime,
t.RECEIVE_TIME2 receiveTime2,
t.MESSAGE_ID messageId,
t.DELETE_FLAG deleteFlag,
t.CREATE_USER createUser,
t.CREATE_USER_ORG createUserOrg,
t.CREATE_USER_DEPT createUserDept,
t.CREATE_DATE createDate,
t.MODIFY_USER modifyUser,
t.MODIFY_DATE modifyDate,
t.EXT_DATE1 extDate1,
t.EXT_DATE2 extDate2,
t.EXT_NUM1 extNum1,
t.EXT_NUM2 extNum2,
t.EXT_NUM3 extNum3,
t.EXT_STR1 extStr1,
t.EXT_STR2 extStr2,
t.EXT_STR3 extStr3,
t.EXT_STR4 extStr4,
t.EXT_STR5 extStr5,
mr.ID mrid,
mr.MAIL_ID mailId,
mr.RECEIVE_USER_ID receiveUserId,
mr.RECEIVE_MAIL receiveMail,
mr.RECEIVE_TYPE receiveType,
mr.READ_FLAG readFlag,
mr.STAR_MAIL starMail,
mr.FOLDER_ID folderId,
mr.READ_DATE readDate,
mr.REMIND_FLAG remindFlag,
mr.RECOVER_FLAG recoverFlag,
u.DISPLAY_NAME displayName,
user1.DISPLAY_NAME senderUserName,
box.id

FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on
t.SENDER_USER_ID=user1.ID
LEFT JOIN tty_ic_mailbox box ON t.MAILBOX_ID = box.ID,
tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID

	t.ID=mr.MAIL_ID

		and t.ID = #{id}


		and t.CONTACTS_ID = #{contactsId}


		and t.MAILBOX_ID = #{mailboxId}


		and t.MAIL_TITLE = #{mailTitle}


		and t.MAIL_CONTENT = #{mailContent}


		and t.SENDER_USER_ID = #{senderUserId}


		and t.SENDER_MAIL = #{senderMail}


		and t.ISFILE = #{isfile}


		and t.STAR_MAIL = #{starMail}


		and t.SMS_ALERT = #{smsAlert}


		and t.SIGNATURE = #{signature}


		and t.PRESSING = #{pressing}


		and t.ENCRYPTION = #{encryption}


		and t.ENCRYPTION_PASS = #{encryptionPass}


		and t.REPLY_TEXTING = #{replyTexting}


		and t.REPLY_TEXTING_TIME = #{replyTextingTime}


		and t.MAIL_STATUS = #{mailStatus}


		and t.SENDER_TIME = #{senderTime}


		and t.RECEIVE_TIME2 = #{receiveTime2}


		and t.MESSAGE_ID = #{messageId}


		and t.MAIL_FOLDER_ID = #{mailFolderId}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}


		and t.RECEIVE_TIME2>=#{searchReceiveTimeBegin}


		and t.RECEIVE_TIME2<=#{searchReceiveTimeEnd}


		and mr.FOLDER_ID !=#{excludeFolderId}


		and

			(1=1

				and t.MAILBOX_ID = #{box.id}

			)




		and

			(1=1 and mr.delete_flag=0

				and
				mr.FOLDER_ID =#{rec.folderId}


				and mr.STAR_MAIL=#{rec.starMail}


				and mr.READ_FLAG=#{rec.readFlag}




						and mr.RECEIVE_MAIL=#{rec.receiveMail}


						and mr.RECEIVE_USER_ID=#{rec.receiveUserId}





					and mr.RECEIVE_MAIL=#{rec.receiveMail}


					and mr.RECEIVE_USER_ID=#{rec.receiveUserId}


			)





	order by ${orderBy}

SELECT count(t.ID)
FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on
t.SENDER_USER_ID=user1.ID
LEFT JOIN tty_ic_mailbox box ON t.MAILBOX_ID = box.ID,
tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID

	t.ID=mr.MAIL_ID

		and t.ID = #{id}


		and t.CONTACTS_ID = #{contactsId}


		and t.MAILBOX_ID = #{mailboxId}


		and t.MAIL_TITLE = #{mailTitle}


		and t.MAIL_CONTENT = #{mailContent}


		and t.SENDER_USER_ID = #{senderUserId}


		and t.SENDER_MAIL = #{senderMail}


		and t.ISFILE = #{isfile}


		and t.STAR_MAIL = #{starMail}


		and t.SMS_ALERT = #{smsAlert}


		and t.SIGNATURE = #{signature}


		and t.PRESSING = #{pressing}


		and t.ENCRYPTION = #{encryption}


		and t.ENCRYPTION_PASS = #{encryptionPass}


		and t.REPLY_TEXTING = #{replyTexting}


		and t.REPLY_TEXTING_TIME = #{replyTextingTime}


		and t.MAIL_STATUS = #{mailStatus}


		and t.SENDER_TIME = #{senderTime}


		and t.RECEIVE_TIME2 = #{receiveTime2}


		and t.MESSAGE_ID = #{messageId}


		and t.MAIL_FOLDER_ID = #{mailFolderId}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}


		and t.RECEIVE_TIME2>=#{searchReceiveTimeBegin}


		and t.RECEIVE_TIME2<=#{searchReceiveTimeEnd}


		and mr.FOLDER_ID !=#{excludeFolderId}


		and

			(1=1

				and t.MAILBOX_ID = #{box.id}

			)




		and

			(1=1 and mr.delete_flag=0

				and
				mr.FOLDER_ID =#{rec.folderId}


				and mr.STAR_MAIL=#{rec.starMail}


				and mr.READ_FLAG=#{rec.readFlag}




						and mr.RECEIVE_MAIL=#{rec.receiveMail}


						and mr.RECEIVE_USER_ID=#{rec.receiveUserId}





					and mr.RECEIVE_MAIL=#{rec.receiveMail}


					and mr.RECEIVE_USER_ID=#{rec.receiveUserId}


			)

SELECT v.id id, v.contactsId contactsId, v.mailboxId mailboxId, v.mailTitle mailTitle, v.mailContent mailContent, v.senderUserId senderUserId, v.senderMail senderMail, v.sendType sendType, v.ISFILE isfile, v.smsAlert smsAlert, v.signature signature, v.pressing pressing, v.encryption encryption, v.encryptionPass encryptionPass, v.replyTexting replyTexting, v.replyTextingTime replyTextingTime, v.mailStatus mailStatus, v.senderTime senderTime, v.receiveTime2 receiveTime2, v.messageId messageId, v.deleteFlag deleteFlag, v.createUser createUser, v.createUserOrg createUserOrg, v.createUserDept createUserDept, v.createDate createDate, v.modifyUser modifyUser, v.modifyDate modifyDate, v.extDate1 extDate1, v.extDate2 extDate2, v.extNum1 extNum1, v.extNum2 extNum2, v.extNum3 extNum3, v.extStr1 extStr1, v.extStr2 extStr2, v.extStr3 extStr3, v.extStr4 extStr4, v.extStr5 extStr5, v.mrid mrid, v.mailId mailId, v.receiveUserId receiveUserId, v.receiveMail receiveMail, v.receiveType receiveType, v.readFlag readFlag, v.starMail starMail, v.folderId folderId, v.readDate readDate, v.displayName displayName, v.senderUserName senderUserName, v.boxId boxId, decode (v.FOLDERID,'3' , (SELECT LISTAGG( su.DISPLAY_NAME,',') WITHIN GROUP(ORDER BY su.DISPLAY_NAME) AS receives FROM tty_ic_mail_record g LEFT JOIN TOA_SYS_USER su ON g.RECEIVE_USER_ID = su.ID WHERE g.MAIL_ID = v.id AND g.RECEIVE_TYPE != 4 ), NULL ) receiveUsers FROM (SELECT t.ID id, t.CONTACTS_ID contactsId, t.MAILBOX_ID mailboxId, t.MAIL_TITLE mailTitle, t.MAIL_CONTENT mailContent, t.SENDER_USER_ID senderUserId, t.SENDER_MAIL senderMail, t.SEND_TYPE sendType, t.ISFILE isfile, t.SMS_ALERT smsAlert, t.SIGNATURE signature, t.PRESSING pressing, t.ENCRYPTION encryption, t.ENCRYPTION_PASS encryptionPass, t.REPLY_TEXTING replyTexting, t.REPLY_TEXTING_TIME replyTextingTime, t.MAIL_STATUS mailStatus, t.SENDER_TIME senderTime, t.RECEIVE_TIME2 receiveTime2, t.MESSAGE_ID messageId, t.DELETE_FLAG deleteFlag, t.CREATE_USER createUser, t.CREATE_USER_ORG createUserOrg, t.CREATE_USER_DEPT createUserDept, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.EXT_DATE1 extDate1, t.EXT_DATE2 extDate2, t.EXT_NUM1 extNum1, t.EXT_NUM2 extNum2, t.EXT_NUM3 extNum3, t.EXT_STR1 extStr1, t.EXT_STR2 extStr2, t.EXT_STR3 extStr3, t.EXT_STR4 extStr4, t.EXT_STR5 extStr5, mr.ID mrid, mr.MAIL_ID mailId, mr.RECEIVE_USER_ID receiveUserId, mr.RECEIVE_MAIL receiveMail, mr.RECEIVE_TYPE receiveType, mr.READ_FLAG readFlag, mr.STAR_MAIL starMail, mr.FOLDER_ID folderId, mr.READ_DATE readDate, u.DISPLAY_NAME displayName, user1.DISPLAY_NAME senderUserName, box.id boxId FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on t.SENDER_USER_ID=user1.ID LEFT JOIN tty_ic_mailbox box ON t.MAILBOX_ID = box.ID, tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID
t.ID=mr.MAIL_ID
and t.ID = #{id}
and t.CONTACTS_ID = #{contactsId}
and t.MAILBOX_ID = #{mailboxId}
and t.MAIL_TITLE like '%${mailTitle}%'
and t.MAIL_CONTENT = #{mailContent}
and t.SENDER_USER_ID = #{senderUserId}
and t.SENDER_MAIL like '%${senderMail}%'
and t.ISFILE = #{isfile}
and t.STAR_MAIL = #{starMail}
and t.SMS_ALERT = #{smsAlert}
and t.SIGNATURE = #{signature}
and t.PRESSING = #{pressing}
and t.ENCRYPTION = #{encryption}
and t.ENCRYPTION_PASS = #{encryptionPass}
and t.REPLY_TEXTING = #{replyTexting}
and t.REPLY_TEXTING_TIME = #{replyTextingTime}
and t.MAIL_STATUS = #{mailStatus}
and t.SENDER_TIME = #{senderTime}
and t.RECEIVE_TIME2 = #{receiveTime2}
and t.MESSAGE_ID = #{messageId}
and t.MAIL_FOLDER_ID = #{mailFolderId}
and t.DELETE_FLAG = #{deleteFlag}
and t.CREATE_USER = #{createUser}
and t.CREATE_USER_ORG = #{createUserOrg}
and t.CREATE_USER_DEPT = #{createUserDept}
and t.CREATE_DATE = #{createDate}
and t.MODIFY_USER = #{modifyUser}
and t.MODIFY_DATE = #{modifyDate}
and t.EXT_DATE1 = #{extDate1}
and t.EXT_DATE2 = #{extDate2}
and t.EXT_NUM1 = #{extNum1}
and t.EXT_NUM2 = #{extNum2}
and t.EXT_NUM3 = #{extNum3}
and t.EXT_STR1 = #{extStr1}
and t.EXT_STR2 = #{extStr2}
and t.EXT_STR3 = #{extStr3}
and t.EXT_STR4 = #{extStr4}
and t.EXT_STR5 = #{extStr5}
and t.RECEIVE_TIME2>=#{searchReceiveTimeBegin}
and t.RECEIVE_TIME2<=#{searchReceiveTimeEnd}
and mr.FOLDER_ID !=#{excludeFolderId}
and
(1=1
and t.MAILBOX_ID = #{box.id}
)
and
(1=1 and mr.delete_flag=0
and mr.FOLDER_ID =#{rec.folderId}
and t.RECEIVE_TIME2 IS NOT NULL
and mr.STAR_MAIL=#{rec.starMail}
and mr.READ_FLAG=#{rec.readFlag}
and mr.RECEIVE_MAIL=#{rec.receiveMail}
and mr.RECEIVE_USER_ID=#{rec.receiveUserId}
and mr.RECEIVE_MAIL=#{rec.receiveMail}
AND mr.MAIL_ID IN( SELECT c.id FROM tty_ic_mail c LEFT JOIN tty_ic_mail_record m ON c.id = m.mail_id WHERE m.RECEIVE_MAIL = #{rec.receiveMailSearch} AND (m.RECEIVE_TYPE=0 OR m.RECEIVE_TYPE=1 OR m.RECEIVE_TYPE = 2 OR m.RECEIVE_TYPE = 3)
AND c.id NOT IN ( SELECT s.ID FROM tty_ic_mail s LEFT JOIN tty_ic_mail_record d ON s.id = d.mail_id WHERE d.RECEIVE_MAIL = #{rec.receiveMailSearch} AND ( d.RECEIVE_TYPE = 0 OR d.RECEIVE_TYPE = 1 OR d.RECEIVE_TYPE = 2 OR d.RECEIVE_TYPE = 3 ) AND ((s.SENDER_MAIL !=#{rec.receiveMail} AND d.receive_type = 2) or (s.SENDER_MAIL !=#{rec.receiveMail} AND d.receive_type = 3) ) )
)
and mr.RECEIVE_USER_ID=#{rec.receiveUserId}
AND mr.MAIL_ID IN( SELECT c.id FROM tty_ic_mail c LEFT JOIN tty_ic_mail_record m ON c.id = m.mail_id WHERE m.RECEIVE_USER_ID = #{rec.receiveUserIdSearch} AND (m.RECEIVE_TYPE=0 OR m.RECEIVE_TYPE=1 OR m.RECEIVE_TYPE = 2 OR m.RECEIVE_TYPE = 3)
AND c.id NOT IN ( SELECT s.ID FROM tty_ic_mail s LEFT JOIN tty_ic_mail_record d ON s.id = d.mail_id WHERE d.RECEIVE_USER_ID = #{rec.receiveUserIdSearch} AND ( d.RECEIVE_TYPE = 0 OR d.RECEIVE_TYPE = 1 OR d.RECEIVE_TYPE = 2 OR d.RECEIVE_TYPE = 3 ) AND ((s.SENDER_USER_ID !=#{rec.receiveUserId} AND d.receive_type = 2) or (s.SENDER_USER_ID !=#{rec.receiveUserId} AND d.receive_type = 3) ) )
)
)
order by ${orderBy}
) v

SELECT count(t.ID)
FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on
t.SENDER_USER_ID=user1.ID
LEFT JOIN tty_ic_mailbox box ON t.MAILBOX_ID = box.ID,
tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID

	t.ID=mr.MAIL_ID


		and t.ID = #{id}


		and t.CONTACTS_ID = #{contactsId}


		and t.MAILBOX_ID = #{mailboxId}


		and t.MAIL_TITLE like '%${mailTitle}%'


		and t.MAIL_CONTENT = #{mailContent}


		and t.SENDER_USER_ID = #{senderUserId}


		and t.SENDER_MAIL like '%${senderMail}%'


		and t.ISFILE = #{isfile}


		and t.STAR_MAIL = #{starMail}


		and t.SMS_ALERT = #{smsAlert}


		and t.SIGNATURE = #{signature}


		and t.PRESSING = #{pressing}


		and t.ENCRYPTION = #{encryption}


		and t.ENCRYPTION_PASS = #{encryptionPass}


		and t.REPLY_TEXTING = #{replyTexting}


		and t.REPLY_TEXTING_TIME = #{replyTextingTime}


		and t.MAIL_STATUS = #{mailStatus}


		and t.SENDER_TIME = #{senderTime}


		and t.RECEIVE_TIME2 = #{receiveTime2}


		and t.MESSAGE_ID = #{messageId}


		and t.MAIL_FOLDER_ID = #{mailFolderId}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}


		and t.RECEIVE_TIME2>=#{searchReceiveTimeBegin}


		and t.RECEIVE_TIME2<=#{searchReceiveTimeEnd}


		and mr.FOLDER_ID !=#{excludeFolderId}


		and

			(1=1

				and t.MAILBOX_ID = #{box.id}

			)




		and

			(1=1 and mr.delete_flag=0

				and
				mr.FOLDER_ID =#{rec.folderId}

					and t.RECEIVE_TIME2 IS NOT NULL



				and mr.STAR_MAIL=#{rec.starMail}


				and mr.READ_FLAG=#{rec.readFlag}




						and mr.RECEIVE_MAIL=#{rec.receiveMail}


						and mr.RECEIVE_USER_ID=#{rec.receiveUserId}





					and mr.RECEIVE_MAIL=#{rec.receiveMail}

						AND mr.MAIL_ID IN(
						SELECT c.id FROM tty_ic_mail c LEFT JOIN tty_ic_mail_record m
						ON c.id = m.mail_id WHERE m.RECEIVE_MAIL =
						#{rec.receiveMailSearch}
						AND (m.RECEIVE_TYPE=0 OR m.RECEIVE_TYPE=1
						OR m.RECEIVE_TYPE = 2 OR m.RECEIVE_TYPE = 3)

							AND c.id NOT IN (
							SELECT
							s.ID
							FROM
							tty_ic_mail s
							LEFT JOIN tty_ic_mail_record d
							ON s.id = d.mail_id
							WHERE d.RECEIVE_MAIL = #{rec.receiveMailSearch}
							AND (
							d.RECEIVE_TYPE = 0
							OR d.RECEIVE_TYPE = 1
							OR d.RECEIVE_TYPE = 2
							OR d.RECEIVE_TYPE = 3
							)
							AND ((s.SENDER_MAIL !=#{rec.receiveMail} AND d.receive_type = 2)
							or (s.SENDER_MAIL !=#{rec.receiveMail} AND d.receive_type = 3)
							)
							)

						)



					and mr.RECEIVE_USER_ID=#{rec.receiveUserId}

						AND mr.MAIL_ID IN(
						SELECT c.id FROM tty_ic_mail c LEFT JOIN tty_ic_mail_record m
						ON c.id = m.mail_id WHERE m.RECEIVE_USER_ID =
						#{rec.receiveUserIdSearch}
						AND (m.RECEIVE_TYPE=0 OR m.RECEIVE_TYPE=1
						OR m.RECEIVE_TYPE = 2 OR m.RECEIVE_TYPE = 3)

							AND c.id NOT IN (
							SELECT
							s.ID
							FROM
							tty_ic_mail s
							LEFT JOIN tty_ic_mail_record d
							ON s.id = d.mail_id
							WHERE d.RECEIVE_USER_ID = #{rec.receiveUserIdSearch}
							AND (
							d.RECEIVE_TYPE = 0
							OR d.RECEIVE_TYPE = 1
							OR d.RECEIVE_TYPE = 2
							OR d.RECEIVE_TYPE = 3
							)
							AND ((s.SENDER_USER_ID !=#{rec.receiveUserId} AND d.receive_type =
							2)
							or (s.SENDER_USER_ID !=#{rec.receiveUserId} AND d.receive_type =
							3)
							)
							)

						)



			)

SELECT
t.ID id,
t.CONTACTS_ID contactsId,
t.MAILBOX_ID mailboxId,
t.MAIL_TITLE mailTitle,
t.MAIL_CONTENT mailContent,
t.SENDER_USER_ID senderUserId,
t.SENDER_MAIL senderMail,
t.SEND_TYPE sendType,
t.ISFILE isfile,
t.SMS_ALERT smsAlert,
t.SIGNATURE signature,
t.PRESSING pressing,
t.ENCRYPTION encryption,
t.ENCRYPTION_PASS encryptionPass,
t.REPLY_TEXTING replyTexting,
t.REPLY_TEXTING_TIME replyTextingTime,
t.MAIL_STATUS mailStatus,
t.SENDER_TIME senderTime,
t.RECEIVE_TIME2 receiveTime2,
t.MESSAGE_ID messageId,
t.DELETE_FLAG deleteFlag,
t.CREATE_USER createUser,
t.CREATE_USER_ORG createUserOrg,
t.CREATE_USER_DEPT createUserDept,
t.CREATE_DATE createDate,
t.MODIFY_USER modifyUser,
t.MODIFY_DATE modifyDate,
t.EXT_DATE1 extDate1,
t.EXT_DATE2 extDate2,
t.EXT_NUM1 extNum1,
t.EXT_NUM2 extNum2,
t.EXT_NUM3 extNum3,
t.EXT_STR1 extStr1,
t.EXT_STR2 extStr2,
t.EXT_STR3 extStr3,
t.EXT_STR4 extStr4,
t.EXT_STR5 extStr5,
mr.ID mrid,
mr.MAIL_ID mailId,
mr.RECEIVE_USER_ID receiveUserId,
mr.RECEIVE_MAIL receiveMail,
mr.RECEIVE_TYPE receiveType,
mr.READ_FLAG readFlag,
mr.STAR_MAIL starMail,
mr.FOLDER_ID folderId,
mr.READ_DATE readDate,
mr.REMIND_FLAG remindFlag,
mr.RECOVER_FLAG recoverFlag,
u.DISPLAY_NAME displayName,
user1.DISPLAY_NAME senderUserName,
box.id,
mr.REPLY_FLAG

FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on
t.SENDER_USER_ID=user1.ID
LEFT JOIN tty_ic_mailbox box ON t.MAILBOX_ID = box.ID,
tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID

	t.ID=mr.MAIL_ID and mr.delete_flag=0
	and t.REPLY_TEXTING = 1
	and
	mr.FOLDER_ID = 1
	and mr.REMIND_FLAG = 0
	and mr.RECEIVE_TYPE <> 4
	and mr.REPLY_FLAG <> 1

		and t.ID = #{id}


		and t.CONTACTS_ID = #{contactsId}


		and t.MAILBOX_ID = #{mailboxId}


		and t.MAIL_TITLE = #{mailTitle}


		and t.MAIL_CONTENT = #{mailContent}


		and t.SENDER_USER_ID = #{senderUserId}


		and t.SENDER_MAIL = #{senderMail}


		and t.ISFILE = #{isfile}


		and t.STAR_MAIL = #{starMail}


		and t.SMS_ALERT = #{smsAlert}


		and t.SIGNATURE = #{signature}


		and t.PRESSING = #{pressing}


		and t.ENCRYPTION = #{encryption}


		and t.ENCRYPTION_PASS = #{encryptionPass}


		and t.REPLY_TEXTING_TIME = #{replyTextingTime}


		and t.MAIL_STATUS = #{mailStatus}


		and t.SENDER_TIME = #{senderTime}


		and t.RECEIVE_TIME2 = #{receiveTime2}


		and t.MESSAGE_ID = #{messageId}


		and t.MAIL_FOLDER_ID = #{mailFolderId}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


