eUser, t.CREATE_USER_ORG createUserOrg, t.CREATE_USER_DEPT createUserDept, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, t.EXT_DATE1 extDate1, t.EXT_DATE2 extDate2, t.EXT_NUM1 extNum1, t.EXT_NUM2 extNum2, t.EXT_NUM3 extNum3, t.EXT_STR1 extStr1, t.EXT_STR2 extStr2, t.EXT_STR3 extStr3, t.EXT_STR4 extStr4, t.EXT_STR5 extStr5, mr.ID mrid, mr.MAIL_ID mailId, mr.RECEIVE_USER_ID receiveUserId, mr.RECEIVE_MAIL receiveMail, mr.RECEIVE_TYPE receiveType, mr.READ_FLAG readFlag, mr.STAR_MAIL starMail, mr.FOLDER_ID folderId, mr.READ_DATE readDate, mr.REMIND_FLAG remindFlag, mr.RECOVER_FLAG recoverFlag, u.DISPLAY_NAME displayName, user1.DISPLAY_NAME senderUserName
FROM tty_ic_mail t LEFT JOIN TOA_SYS_USER user1 on t.SENDER_USER_ID=user1.ID ,tty_ic_mail_record mr left join TOA_SYS_USER u on mr.RECEIVE_USER_ID=u.ID
t.ID=mr.MAIL_ID
and t.ID = #{id}
and t.CONTACTS_ID = #{contactsId}
and t.MAILBOX_ID = #{mailboxId}
