and
(1=1
and t.MAILBOX_ID = #{box.id}
)
and
(1=1 and mr.delete_flag=0 and mr.READ_FLAG=1
and mr.STAR_MAIL=#{rec.starMail}
and mr.RECEIVE_MAIL=#{rec.receiveMail}
