		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

SELECT a.CREATE_USER createUser,COUNT(a.create_user) num  
FROM((SELECT t.CREATE_USER 
	  FROM smsserver_out t	

	t.SENT_DATE IS  NULL
	and t.status  != 'F'

		 and t.CREATE_DATE >= #{outDateStart} 


		 and t.CREATE_DATE <= #{outDateEnd} 


)UNION ALL
(SELECT 
	t.CREATE_USER
FROM smsserver_out t


	 t.status  != 'F'

		 and t.SENT_DATE >= #{outDateStart} 


		 and t.SENT_DATE <= #{outDateEnd} 


		and t.CREATE_USER = #{createUser}


) ) a GROUP BY  a.CREATE_USER

		order by ${orderBy}

SELECT COUNT(o.create_user) FROM 
(SELECT a.CREATE_USER  FROM((SELECT 
	t.CREATE_USER 
	FROM smsserver_out t

	t.SENT_DATE IS  NULL
	and t.status  != 'F'

		 and t.CREATE_DATE >= #{outDateStart} 


		 and t.CREATE_DATE <= #{outDateEnd} 


	)UNION ALL
	(SELECT t.CREATE_USER 
	FROM smsserver_out t

	 t.status  != 'F'

		 and t.SENT_DATE >= #{outDateStart} 


		 and t.SENT_DATE <= #{outDateEnd} 


		and t.CREATE_USER = #{createUser}


) ) a GROUP BY  a.CREATE_USER
) o

SELECT COUNT(a.create_user) num  
FROM((SELECT t.CREATE_USER 
	  FROM smsserver_out t	

	t.SENT_DATE IS  NULL
	and t.status  != 'F'

		 and t.CREATE_DATE >= #{outDateStart} 


		 and t.CREATE_DATE <= #{outDateEnd} 


)UNION ALL
(SELECT 
	t.CREATE_USER
FROM smsserver_out t


	 t.status  != 'F'

		 and t.SENT_DATE >= #{outDateStart} 


		 and t.SENT_DATE <= #{outDateEnd} 


		and t.CREATE_USER = #{createUser}


) ) a 


		and a.CREATE_USER  = #{createUser}


GROUP BY  a.CREATE_USER 

		order by ${orderBy}

mapper.delete:
    	delete from smsserver_out
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_sys_remind t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
