mapper.select:
SELECT 
	t.ID id,
	t.TYPE type,
	t.SET_TYPE setType,
	t.RANK_ID rankId,
	t.START_DATE startDate,
	t.MAXNUM maxnum,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	t.EXT_DATE1 extDate1,
	t.EXT_DATE2 extDate2,
	t.EXT_NUM1 extNum1,
	t.EXT_NUM2 extNum2,
	t.EXT_NUM3 extNum3,
	t.EXT_STR1 extStr1,
	t.EXT_STR2 extStr2,
	t.EXT_STR3 extStr3,
	t.EXT_STR4 extStr4,
	t.EXT_STR5 extStr5

FROM tty_smsserver_set t


		and t.ID = #{id}


		and t.TYPE = #{type}


		and t.SET_TYPE = #{setType}


		and t.RANK_ID = #{rankId}



		 and t.START_DATE >= #{setDateStart} 


		 and t.START_DATE <= #{setDateEnd} 


		and t.MAXNUM = #{maxnum}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}



		order by ${orderBy}

SELECT count(ID) 
FROM tty_smsserver_set t


		and t.ID = #{id}


		and t.TYPE = #{type}


		and t.SET_TYPE = #{setType}


		and t.RANK_ID = #{rankId}


		 and t.START_DATE >= #{setDateStart} 


		 and t.START_DATE <= #{setDateEnd} 


		and t.MAXNUM = #{maxnum}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		and t.EXT_DATE1 = #{extDate1}


		and t.EXT_DATE2 = #{extDate2}


		and t.EXT_NUM1 = #{extNum1}


		and t.EXT_NUM2 = #{extNum2}


		and t.EXT_NUM3 = #{extNum3}


		and t.EXT_STR1 = #{extStr1}


		and t.EXT_STR2 = #{extStr2}


		and t.EXT_STR3 = #{extStr3}


		and t.EXT_STR4 = #{extStr4}


		and t.EXT_STR5 = #{extStr5}

mapper.delete:
    	delete from tty_smsserver_set
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE tty_smsserver_set t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

    	UPDATE tty_smsserver_set t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}
