mapper.select:
		SELECT 
		 t.ID id
		,t.machine_number machineNumber
		,t.describes describes
		,t.serial_number serialNumber
		,t.application_date applicationDate
		,t.arrival_date arrivalDate
		,t.installs installs
		,t.address address
		,t.REMARK remark
		,t.DELETE_FLAG deleteFlag
		,t.CREATE_USER createUser
		,t.CREATE_USER_DEPT createUserDept
		,t.CREATE_DATE createDate
		,t.MODIFY_USER modifyUser
		,t.MODIFY_DATE modifyDate
		,t.EXT_STR1 extStr1
		,t.EXT_STR2 extStr2
		,t.EXT_STR3 extStr3
		,t.EXT_STR4 extStr4
		,t.EXT_STR5 extStr5
		,t.EXT_DATE1 extDate1
		,t.EXT_DATE2 extDate2
		,t.EXT_NUM1 extNum1
		,t.EXT_NUM2 extNum2
		,t.EXT_NUM3 extNum3
 FROM toa_network_procure t


		and t.id=#{id}


		and t.machine_number=#{machineNumber}


		and t.describes=#{describes}


		and t.serial_number=#{serialNumber}


		and t.application_date=#{applicationDate}


		and t.arrival_date=#{arrivalDate}


		and t.installs=#{installs}


		and t.address=#{address}


		and t.REMARK=#{remark}


		and t.DELETE_FLAG=#{deleteFlag}


		and t.CREATE_USER=#{createUser}


		and t.CREATE_USER_DEPT=#{createUserDept}


		and t.CREATE_DATE=#{createDate}


		and t.MODIFY_USER=#{modifyUser}


		and t.MODIFY_DATE=#{modifyDate}


		and t.EXT_STR1=#{extStr1}


		and t.EXT_STR2=#{extStr2}


		and t.EXT_STR3=#{extStr3}


		and t.EXT_STR4=#{extStr4}


		and t.EXT_STR5=#{extStr5}


		and t.EXT_DATE1=#{extDate1}


		and t.EXT_DATE2=#{extDate2}


		and t.EXT_NUM1=#{extNum1}


		and t.EXT_NUM2=#{extNum2}


		and t.EXT_NUM3=#{extNum3}



			order by ${orderBy}

	SELECT COUNT(ID) 
 FROM toa_network_procure t


	and t.id=#{id}


	and t.machine_number=#{machineNumber}


	and t.describes=#{describes}


	and t.serial_number=#{serialNumber}


	and t.application_date=#{applicationDate}


	and t.arrival_date=#{arrivalDate}


	and t.installs=#{installs}


	and t.address=#{address}


	and t.REMARK=#{remark}


	and t.DELETE_FLAG=#{deleteFlag}


	and t.CREATE_USER=#{createUser}


	and t.CREATE_USER_DEPT=#{createUserDept}


	and t.CREATE_DATE=#{createDate}


	and t.MODIFY_USER=#{modifyUser}


	and t.MODIFY_DATE=#{modifyDate}


	and t.EXT_STR1=#{extStr1}


	and t.EXT_STR2=#{extStr2}


	and t.EXT_STR3=#{extStr3}


	and t.EXT_STR4=#{extStr4}


	and t.EXT_STR5=#{extStr5}


	and t.EXT_DATE1=#{extDate1}


	and t.EXT_DATE2=#{extDate2}


	and t.EXT_NUM1=#{extNum1}


	and t.EXT_NUM2=#{extNum2}


	and t.EXT_NUM3=#{extNum3}

mapper.update:
	     UPDATE toa_network_procure t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
 			where ID in

				 #{primaryKey}
