mapper.select:
	SELECT 
		 t.ID id
		,t.customers_id customersId
		,t.customers_name customersName
		,t.phone phone
		,t.linkman linkman
		,t.address address
		,t.taxid taxid
		,t.expand_people expandPeople
		,t.vask vask
		,t.supporter supporter
		,t.cooperate_type cooperateType
		,t.business_type businessType
		,t.user_name userName
		,t.speed_name speedName
		,t.stick stick
		,t.charge_mode chargeMode
		,t.price price
		,t.floored_number flooredNumber
		,t.charge_number chargeNumber
		,t.amount amount
		,t.charge_time chargeTime
		,t.start_date startDate
		,t.start_end startEnd
		,t.remark remark
		,t.DELETE_FLAG deleteFlag
		,t.CREATE_USER createUser
		,t.CREATE_USER_DEPT createUserDept
		,t.CREATE_DATE createDate
		,t.MODIFY_USER modifyUser
		,t.MODIFY_DATE modifyDate
		,t.EXT_STR1 extStr1
		,t.EXT_STR2 extStr2
		,t.EXT_STR3 extStr3
		,t.EXT_STR4 extStr4
		,t.EXT_STR5 extStr5
		,t.EXT_DATE1 extDate1
		,t.EXT_DATE2 extDate2
		,t.EXT_NUM1 extNum1
		,t.EXT_NUM2 extNum2
		,t.EXT_NUM3 extNum3
 FROM toa_product_cdn t


		and t.id=#{id}


		and t.customers_id=#{customersId}


		and t.customers_name=#{customersName}


		and t.phone=#{phone}


		and t.linkman=#{linkman}


		and t.address=#{address}


		and t.taxid=#{taxid}


		and t.expand_people=#{expandPeople}


		and t.vask=#{vask}


		and t.supporter=#{supporter}


		and t.cooperate_type=#{cooperateType}


		and t.business_type=#{businessType}


		and t.user_name=#{userName}


		and t.speed_name=#{speedName}


		and t.stick=#{stick}


		and t.charge_mode=#{chargeMode}


		and t.price=#{price}


		and t.floored_number=#{flooredNumber}


		and t.charge_number=#{chargeNumber}


		and t.amount=#{amount}


		and t.charge_time=#{chargeTime}


		and t.start_date=#{startDate}


		and t.start_end=#{startEnd}


		and t.remark=#{remark}


		and t.DELETE_FLAG=#{deleteFlag}


		and t.CREATE_USER=#{createUser}


		and t.CREATE_USER_DEPT=#{createUserDept}


		and t.CREATE_DATE=#{createDate}


		and t.MODIFY_USER=#{modifyUser}


		and t.MODIFY_DATE=#{modifyDate}


		and t.EXT_STR1=#{extStr1}


		and t.EXT_STR2=#{extStr2}


		and t.EXT_STR3=#{extStr3}


		and t.EXT_STR4=#{extStr4}


		and t.EXT_STR5=#{extStr5}


		and t.EXT_DATE1=#{extDate1}


		and t.EXT_DATE2=#{extDate2}


		and t.EXT_NUM1=#{extNum1}


		and t.EXT_NUM2=#{extNum2}


		and t.EXT_NUM3=#{extNum3}

