mapper.select:
SELECT 
	t.id id,
	t.code code,
	t.value value,
	t.parent_id parentId,
	t.dic_flag dicFlag
FROM tty_sys_dics t


		and t.id = #{id}


		and t.code = #{code}


		and t.value = #{value}


		and t.parent_id = #{parentId}

	and t.type_flag = 1

SELECT count(id) 
FROM tty_sys_dics t


		and t.id = #{id}


		and t.code = #{code}


		and t.value = #{value}


		and t.parent_id = #{parentId}

	and t.type_flag = 1

mapper.delete:
    	delete from tty_sys_dics


				and id = #{id}


				and code = #{code}

			and type_flag = 1
