mapper.select:
SELECT 
	t.id id,
	t.group_id groupId,
	t.user_id userId

FROM toa_sys_group_user t


		and t.id = #{id}



		and t.group_id = #{groupId}



		and t.user_id = #{userId}

SELECT count(id) 
FROM toa_sys_group_user t


		and t.id = #{id}



		and t.group_id = #{groupId}



		and t.user_id = #{userId}

mapper.delete:
    	delete from toa_sys_group_user
      		where group_id in

	            #{primaryKey}
