mapper.select:
SELECT 
	t.ID id,
	t.CONTENT content,
	t.PHRASE_TYPE phraseType,
	t.DELETE_FLAG deleteFlag,
	t.CREATE_USER createUser,
	t.CREATE_USER_DEPT createUserDept,
	t.CREATE_USER_ORG createUserOrg,
	t.CREATE_DATE createDate,
	t.MODIFY_USER modifyUser,
	t.MODIFY_DATE modifyDate,
	u.display_name userName

FROM toa_sys_phrase t, toa_sys_user u

	t.CREATE_USER = u.ID

		and t.ID = #{id}


		and t.CONTENT = #{content}


		and t.PHRASE_TYPE = #{phraseType}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}


		order by ${orderBy}

SELECT count(t.ID) 
FROM toa_sys_phrase t, toa_sys_user u

	t.CREATE_USER = u.ID

		and t.ID = #{id}


		and t.CONTENT = #{content}


		and t.PHRASE_TYPE = #{phraseType}


		and t.DELETE_FLAG = #{deleteFlag}


		and t.CREATE_USER = #{createUser}


		and t.CREATE_USER_DEPT = #{createUserDept}


		and t.CREATE_USER_ORG = #{createUserOrg}


		and t.CREATE_DATE = #{createDate}


		and t.MODIFY_USER = #{modifyUser}


		and t.MODIFY_DATE = #{modifyDate}

mapper.delete:
    	delete from toa_sys_phrase
      		where ID in

	            #{primaryKey}

mapper.update:
    	UPDATE toa_sys_phrase t set t.delete_flag = 1,t.modify_user = #{modifyUser},t.modify_date = #{modifyDate}
      		where ID in

	            #{primaryKey}

mapper.select:
SELECT t.ID id, t.CONTENT content, t.PHRASE_TYPE phraseType, t.DELETE_FLAG deleteFlag, t.CREATE_USER createUser, t.CREATE_USER_DEPT createUserDept, t.CREATE_USER_ORG createUserOrg, t.CREATE_DATE createDate, t.MODIFY_USER modifyUser, t.MODIFY_DATE modifyDate, u.display_name userName
FROM toa_sys_phrase t, toa_sys_user u
t.CREATE_USER = u.ID
and (t.PHRASE_TYPE = #{phraseType} or t.CREATE_USER = #{createUser})
and t.CREATE_USER = #{createUser}
and t.DELETE_FLAG = #{deleteFlag}
order by ${orderBy}

SELECT count(t.ID) 
FROM toa_sys_phrase t, toa_sys_user u

	t.CREATE_USER = u.ID


			and (t.PHRASE_TYPE = #{phraseType} or t.CREATE_USER = #{createUser})


			and t.CREATE_USER = #{createUser}



		and t.DELETE_FLAG = #{deleteFlag}


		order by ${orderBy}
